import React, { useEffect, useState } from "react";
import axios from "axios";

const Enquiries = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [readStatus, setReadStatus] = useState(() => {
    // Retrieve the read status from local storage
    const savedStatus = localStorage.getItem("readStatus");
    return savedStatus ? JSON.parse(savedStatus) : {};
  });

  useEffect(() => {
    axios
      .get("http://localhost:5001/api/enquiries")
      .then((response) => {
        setEnquiries(response.data);
      })
      .catch((error) => console.error("Error fetching enquiries:", error));
  }, []);

  // Update read status and save to local storage
  const toggleReadStatus = (id) => {
    const updatedStatus = { ...readStatus, [id]: !readStatus[id] };
    setReadStatus(updatedStatus);
    localStorage.setItem("readStatus", JSON.stringify(updatedStatus));
  };

  return (
    <div className="enquiries-container">
      {enquiries.map((enquiry) => (
        <div key={enquiry._id} className="enquiry-card">
          <h3>{enquiry.title}</h3>
          <p><strong>Email:</strong> {enquiry.email}</p>
          <p><strong>Mobile:</strong> {enquiry.mobile}</p>
          <p><strong>City:</strong> {enquiry.city}</p>
          <p><strong>Description:</strong> {enquiry.description}</p>
          {/* Add more fields as needed */}
          <div className="checkbox-container">
            <label>
              <input
                type="checkbox"
                checked={readStatus[enquiry._id] || false}
                onChange={() => toggleReadStatus(enquiry._id)}
              />
              Read
            </label>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Enquiries;
