// PumpGallery.js
import React, { useState, useEffect } from 'react';
import styles from './PumpGallery.module.css';

const PumpGallery = () => {
  const [pumps, setPumps] = useState([]);

  const pumpData = [
    {
      id: 1,
      name: 'Residential Pump Model A',
      description: 'Efficient and reliable for residential use.',
      imageUrl: 'images/pump1.png',
    },
    {
      id: 2,
      name: 'Agricultural Pump Model B',
      description: 'High capacity for agricultural needs.',
      imageUrl: 'images/pump2.png',
    },
    {
      id: 3,
      name: 'Industrial Pump Model C',
      description: 'Built for industrial operations.',
      imageUrl: 'images/pump3.png',
    },
  ];

  useEffect(() => {
    setTimeout(() => {
      setPumps(pumpData);
    }, 500);
  }, []);

  return (
    <div className={styles.pumpGallery}>
      {pumps.length > 0 ? (
        pumps.map((pump) => (
          <div key={pump.id} className={styles.pumpItem}>
            <img src={'./a1.jpg'} alt={pump.name} className={styles.pumpImage} />
            <h3 className={styles.pumpText}>{pump.name}</h3>
            <p className={styles.pumpText}>{pump.description}</p>
            <button className={styles.pumpButton}>Learn More</button>
          </div>
        ))
      ) : (
        <p>Loading pumps...</p>
      )}
    </div>
  );
};

export default PumpGallery;
