import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Details = () => {
    const { pumpId } = useParams();
    const navigate = useNavigate();
    const [pump, setPump] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [pumps, setPumps] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5001/api/agpumps')
            .then(response => setPumps(response.data))
            .catch(error => console.error('Error fetching agricultural pumps:', error));
    }, []);

    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/agpumps/${pumpId}`);
                if (!response.ok) {
                    throw new Error('Agricultural pump not found');
                }
                const data = await response.json();
                setPump(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };
        fetchPumpDetails();
    }, [pumpId]);

    if (loading) return (
        <div className="min-h-screen flex items-center justify-center">
            <div className="text-xl">Loading...</div>
        </div>
    );

    if (error) return (
        <div className="min-h-screen flex items-center justify-center">
            <div>{error}. Please go back to the <a href="/home" className="text-blue-500 hover:underline">Home page</a></div>
        </div>
    );

    if (!pump) return (
        <div className="min-h-screen flex items-center justify-center">
            <div>Agricultural pump not found. Please go back to the <a href="/home" className="text-blue-500 hover:underline">Home page</a></div>
        </div>
    );

    const pumpIds = pumps.map(pump => pump.id);
    const currentIndex = pumpIds.indexOf(pumpId);
    const prevPump = pumpIds[currentIndex - 1] || pumpIds[pumpIds.length - 1];
    const nextPump = pumpIds[currentIndex + 1] || pumpIds[0];

    const handleNavigation = (id) => {
        navigate(`/pumpDetails/${id}`);
    };

    return (
        <div className="min-h-screen bg-white">
            <div className="container mx-auto px-4 py-8 max-w-7xl">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                    {/* Left Column */}
                    <div className="flex flex-col h-full">
                        {/* Main Image Container */}
                        <div className="w-full h-[400px] bg-gray-50 rounded-lg mb-6">
                            <img 
                                src={`http://localhost:5001${pump.image}`}
                                className="w-full h-full object-contain"
                                alt={pump.name}
                                onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = '/placeholder-image.png';
                                }}
                            />
                        </div>
                        {/* Product Info */}
                        <div className="flex flex-col gap-4">
                            <h1 className="text-2xl font-bold text-gray-900">{pump.name}</h1>
                            <p className="text-lg text-gray-700">
                                <strong>MRP:</strong> {pump.price} (Inclusive of all taxes)
                            </p>
                            <a 
                                href="https://lallytradingcompany.onrender.com"
                                className="w-full md:w-auto text-center bg-blue-600 text-white px-8 py-3 rounded-md hover:bg-blue-700 transition-colors"
                            >
                                Send Enquiry
                            </a>
                        </div>
                    </div>

                    {/* Right Column */}
                    <div className="flex flex-col h-full">
                        {/* Features Section */}
                        <div className="bg-gray-50 rounded-lg p-6 mb-6">
                            <h2 className="text-xl font-semibold text-gray-900 mb-4">Features</h2>
                            <ul className="space-y-3">
                                {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                    pump.features.map((feature, index) => (
                                        <li key={index} className="flex items-start text-gray-700">
                                            <span className="mr-2 text-blue-600">•</span>
                                            <span>{feature}</span>
                                        </li>
                                    ))
                                ) : (
                                    <li className="text-gray-700">No features available.</li>
                                )}
                            </ul>
                        </div>
                        {/* Right View Image */}
                        <div className="w-full h-[400px] bg-gray-50 rounded-lg">
                            <img 
                                src={`http://localhost:5001${pump.rightImage}`}
                                className="w-full h-full object-contain"
                                alt="Right view of pump"
                                onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = '/placeholder-image.png';
                                }}
                            />
                        </div>
                    </div>
                </div>

                {/* Navigation Buttons */}
                <div className="flex justify-between items-center mt-8 px-4">
                    <button 
                        onClick={() => handleNavigation(prevPump)}
                        className="w-12 h-12 flex items-center justify-center rounded-full bg-gray-100 hover:bg-gray-200 transition-colors"
                    >
                        ←
                    </button>
                    <button 
                        onClick={() => handleNavigation(nextPump)}
                        className="w-12 h-12 flex items-center justify-center rounded-full bg-gray-100 hover:bg-gray-200 transition-colors"
                    >
                        →
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Details;
