import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import prod from '../images/prod.png';
import pump2 from '../images/pump2.png';
import bg2 from '../images/Pump3.jpg';
import bg3 from '../images/Pump2.jpg';
import '../css/styles.css';

function Home() {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = [bg2, bg3];
  const navigate = useNavigate();
  const [inProgressSites, setInProgressSites] = useState(0);
  const [pumpsSold, setPumpsSold] = useState(0);

  useEffect(() => {
    axios
      .get('https://pumpsbackend.onrender.com/api/pumps')
      .then(response => setResidentialPumps(response.data))
      .catch(error => console.error('Error fetching residential pumps:', error));
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 4000);

    return () => clearInterval(interval);
  }, [images.length]);

  const handleScroll = () => {
    const elements = document.querySelectorAll('.scroll-animate');
    elements.forEach((el) => {
      const rect = el.getBoundingClientRect();
      if (rect.top < window.innerHeight - 100) {
        el.classList.add('slide-in');
      }
    });
  };

  useEffect(() => {
    const heroSection = document.querySelector('.hero-slider-section');
    if (heroSection) {
      heroSection.classList.add('slide-in');
    }
  }, []);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    const animateNumbers = (start, end, duration, setter) => {
      const range = end - start;
      const increment = Math.max(1, range / (duration / 30));
      let current = start;

      const timer = setInterval(() => {
        current += increment;
        if (current >= end) {
          current = end;
          clearInterval(timer);
        }
        setter(Math.round(current));
      }, 30);
    };

    const animateToTen = (duration, setter) => {
      const stepDuration = duration / 10;
      let current = 0;

      const timer = setInterval(() => {
        current += 1;
        setter(current);
        if (current >= 10) {
          clearInterval(timer);
        }
      }, stepDuration);
    };

    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            const sharedDuration = 2000;
            animateToTen(sharedDuration, setInProgressSites);
            animateNumbers(0, 12000, sharedDuration, setPumpsSold);
            observer.disconnect();
          }
        });
      },
      { threshold: 0.5 }
    );

    const target = document.querySelector('.stats-section');
    if (target) observer.observe(target);

    return () => observer.disconnect();
  }, [setInProgressSites, setPumpsSold]);

  const handlePumpClick = (pumpId) => {
    navigate(`/pumpDetail/${pumpId}`);
  };

  return (
    <div className="home-container">
      <section className="hero-slider-section scroll-animate">
        <div className="hero-slider">
          <div
            className="hero-slider__track"
            style={{
              transform: `translateX(-${currentIndex * 100}%)`,
              transition: "transform 1s ease-in-out",
            }}
          >
            {images.map((image, index) => (
              <img
                key={index}
                src={image}
                alt={`Slide ${index + 1}`}
                className="hero-slider__image"
              />
            ))}
          </div>
        </div>
      </section>

      <section className="welcome-section scroll-animate">
        <h1 className="welcome-section__title">Welcome to Our Store</h1>
        <p className="welcome-section__text">
          Explore our range of Agricultural submersible pumps<br />
          The product range covers a wide application such as agriculture,
          water supply to townships, industries, sewage, and drainage.
        </p>
        <Link to="/about" className="welcome-section__button">Read more</Link>
      </section>

      <section className="products-section">
        <h2 className="products-section__title scroll-animate">Residential Submersible Pumps</h2>
        <div className="products-grid">
          {residentialPumps.length > 0 ? (
            residentialPumps.map((pump) => (
              <article 
                key={pump.id} 
                className="product-card scroll-animate"
                onClick={() => handlePumpClick(pump.id)}
              >
                <img
                  src={`https://pumpsbackend.onrender.com/uploads/${pump.image}`}
                  alt={pump.name}
                  className="product-card__image"
                  onMouseOver={(e) => e.currentTarget.classList.add('image-hover-effect')}
                  onMouseOut={(e) => e.currentTarget.classList.remove('image-hover-effect')}
                />
                <div className="product-card__content">
                  <h3 className="product-card__title">{pump.name}</h3>
                  <p className="product-card__price">{pump.price}</p>
                  <Link to={`/pumpDetail/${pump.id}`} className="product-card__button">Read more</Link>
                </div>
              </article>
            ))
          ) : (
            <p className="products-grid__loading">Loading Residential Pumps...</p>
          )}
        </div>
      </section>

      <section className="stats-section scroll-animate">
        <div className="stats-section__content">
          <div className="stats-section__header">
            <h3 className="stats-section__subtitle">About our company</h3>
            <h2 className="stats-section__title">We provide professional solutions to choose a suitable pump</h2>
            <p className="stats-section__description">
              The product range covers a wide application<br />
              such as domestic, agriculture, water supply to<br />
              townships, industries, sewage, and drainage.
            </p>
          </div>
          <div className="stats-grid">
            <div className="stats-card">
              <h1 className="stats-card__number">{inProgressSites}</h1>
              <p className="stats-card__label">In-Progress Sites.</p>
            </div>
            <div className="stats-card">
              <h1 className="stats-card__number">{pumpsSold.toLocaleString()}+</h1>
              <p className="stats-card__label">Pumps Sold.</p>
            </div>
          </div>
        </div>
        <div className="stats-section__image">
          <img src={pump2} alt="Pump Showcase" />
        </div>
      </section>

      <section className="cta-section scroll-animate">
        <h2 className="cta-section__title">
          We Believe Every Client Is a<br />Valuable Long-Term Partner
        </h2>
      </section>

      <section className="showcase-section scroll-animate">
        <img src={prod} alt="Product Display" className="showcase-section__image" />
      </section>
    </div>
  );
}

export default Home;
