import React, { useState, useEffect } from 'react';
import axios from 'axios';

const PumpGallery = () => {
  const [pumps, setPumps] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data.pumps);
      } catch (err) {
        console.error('Error fetching pumps:', err);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  return (
    <div>
      <h1>Pump Gallery</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <div className="gallery">
        {pumps.map(pump => (
          <div key={pump._id}>
            <img src={`http://localhost:5001${pump.image}`} alt={pump.name} width="150" />
            <h2>{pump.name}</h2>
            <p>{pump.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PumpGallery;
