import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { pumps } from '../data/pumps';

const PumpDetail = () => {
  const { pumpId } = useParams();
  const navigate = useNavigate();
  const pump = pumps.find(p => p.id === pumpId);

  // Get the list of pump ids
  const pumpIds = pumps.map(p => p.id);

  const currentIndex = pumpIds.indexOf(pumpId);
  const prevPumpId = pumpIds[(currentIndex - 1 + pumpIds.length) % pumpIds.length]; // Wrap around if at the start
  const nextPumpId = pumpIds[(currentIndex + 1) % pumpIds.length]; // Wrap around if at the end

  const handleNavigate = (id) => {
    navigate(`/pumpDetail/${id}`);
  };

  if (!pump) {
    return <div>Pump not found</div>;
  }

  return (
    <div className="body">
      <div className="product-description">
        <img src={pump.image} className="product-image" alt={pump.name} />
        <div>
          <h1>{pump.name}</h1>
          <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

          <div className="features">
            <h2>Features</h2>
            <ul>
              {pump.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>

          <a href="https://lallytradingcompany.onrender.com" className="buy-button">Send Enquiry</a>
        </div>
      </div>
      <div className="chart">
        <img src={pump.rightImage} alt="Right view of pump" />
      </div>

      {/* Back and Forward Buttons */}
      <div className="navigation-buttons">
        <button onClick={() => handleNavigate(prevPumpId)} className="prev-btn">Back</button>
        <button onClick={() => handleNavigate(nextPumpId)} className="next-btn">Next</button>
      </div>
    </div>
  );
};

export default PumpDetail;
