import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { PumpContext } from '../context/PumpContext';
import '../css/read.css';

const PumpDetail = () => {
    const { pumpId } = useParams();
    const { pumps } = useContext(PumpContext);

    const pump = pumps.find((p) => p.id === pumpId);

    if (!pump) {
        return <div>Pump not found. Please go back to the <a href="/products">products page</a>.</div>;
    }

    return (
        <div className="body">
            <div className="product-description">
                <img src={`http://localhost:5001/uploads/${pump.image}`} className="product-image" alt={pump.name} />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>
                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {pump.features.map((feature, index) => (
                                <li key={index}>{feature}</li>
                            ))}
                        </ul>
                    </div>
                    <a href="https://lallytradingcompany.onrender.com" className="buy-button">Send Enquiry</a>
                </div>
            </div>
        </div>
    );
};

export default PumpDetail;
