import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/styl.css';

const Products = () => {
  const [pumps, setPumps] = useState([]);

  const importAll = (r) => {
    let images = {};
    r.keys().forEach((item) => {
      images[item.replace('./', '')] = r(item);
    });
    return images;
  };

  const images = importAll(require.context('../images', false, /\.(png|jpe?g|svg)$/));

  const pumpData = [
    { id: 'v50', name: 'V-50 feet Per Stage Borewell Submersible Pumps', image: images['a1.png'], price: 'MRP ₹79,000.00',  },
    { id: 'v5', name: 'V-5 Stainless Steel Borewell Submersible Pumps', image: images['a2.png'], price: 'MRP ₹67,400.00',  },
    { id: 'janta', name: 'Janta Series Borewell Submersible Pumps', image: images['a3.png'], price: 'MRP ₹66,575.00',  },
    { id: 'v-4', name: 'V-4 Stainless Steel Pumps', image: images['a4.png'], price: 'MRP ₹61,300.00',  },
    { id: 'v-6', name: 'V-6 Water Filled Borewell Submersible Pumps', image: images['a5.png'], price: 'MRP ₹45,300.00',  },
    { id: 'v-5', name: 'V-5 Water filled Motor', image: images['v6.png'], price: 'MRP ₹36,925.00',  },
    { id: 'v-4.', name: 'V-4 Water filled motor Submersible Pumps', image: images['v7.png'], price: 'MRP ₹35,200.00',  },
    { id: '1.5h', name: 'Horizontal Openwell Submersible Pumps (1.5 Hp)', image: images['v8.png'], price: 'MRP ₹21,600.00',  },
  ];

  useEffect(() => {
    setPumps(pumpData);

    const handleScroll = () => {
      const elements = document.querySelectorAll('.animate-on-scroll');
      elements.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          el.classList.add('slide-in');
        }
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div>
      <div className="main-content" id="mainContent">
        <div className="product-info">
          <div className="container1">
            <div>
              <h2>Agricultural Products</h2>
            </div>
            <div>
              <p>We offer a wide range of Agricultural submersible pumps to suit your needs.</p>
            </div>
          </div>
        </div>

        <div className="pump-gallery" id="pumpGallery">
          {pumps.map((pump, index) => (
            <div key={index} className="pump-item animate-on-scroll">
              <img src={pump.image} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Products;
