import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css'; // Assuming you have a CSS file for styling

const PumpDetail = () => {
    const { pumpId } = useParams(); // Extract pumpId from URL
    const navigate = useNavigate();

    const [pumps, setPumps] = useState([]); // Initialize pumps as an empty array
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state

    // Fetch all pumps on component mount
    useEffect(() => {
        const fetchPumps = async () => {
            try {
                const response = await fetch('http://localhost:5001/api/pumps');
                if (!response.ok) {
                    throw new Error('Failed to fetch pumps');
                }
                const data = await response.json();
                console.log('Fetched Pumps:', data.pumps); // Log fetched pumps data
                setPumps(data.pumps || []); // Safely set pumps data (default to empty array if undefined)
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchPumps(); // Fetch all pumps data
    }, []); // Run only once when component mounts

    // If still loading
    if (loading) {
        return <div>Loading...</div>; // Show loading message while fetching data
    }

    // If there is an error
    if (error) {
        return <div>{error}. Please go back to the <a href="/products">products page</a>.</div>;
    }

    console.log('Pumps:', pumps); // Log the pumps state to ensure data is correctly set

    // If no pump data or no pump is found by pumpId
    const currentPump = pumps.length > 0 ? pumps.find(pump => pump.id === pumpId) : null;

    console.log('Current Pump:', currentPump); // Log the current pump to check if it's found

    // If pump data is not found
    if (!currentPump) {
        return <div>Pump not found. Please go back to the <a href="/products">products page</a>.</div>;
    }

    // Find the index of the current pump in the list
    const currentIndex = pumps.indexOf(currentPump);

    // Determine the previous and next pump IDs (circular navigation)
    const prevPump = pumps[(currentIndex - 1 + pumps.length) % pumps.length];
    const nextPump = pumps[(currentIndex + 1) % pumps.length];

    // Function to navigate to a specific pump detail
    const handleNavigation = (id) => {
        navigate(`/pumpDetail/${id}`);
    };

    return (
        <div className="body">
            {/* Product Description */}
            <div className="product-description">
                <img 
                    src={`http://localhost:5001/uploads/${currentPump.image}`} 
                    className="product-image" 
                    alt={currentPump.name} 
                />
                <div>
                    <h1>{currentPump.name}</h1>
                    <p><strong>MRP:</strong> {currentPump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        {Array.isArray(currentPump.features) && currentPump.features.length > 0 ? (
                            <ul>
                                {currentPump.features.map((feature, index) => (
                                    <li key={index}>{feature}</li>
                                ))}
                            </ul>
                        ) : (
                            <p>No features available.</p>
                        )}
                    </div>

                    {/* Send enquiry button */}
                    <a 
                        href="https://lallytradingcompany.onrender.com" 
                        className="buy-button"
                    >
                        Send Enquiry
                    </a>
                </div>
            </div>

            {/* Image for right view of the pump */}
            <div className="chart">
                <img 
                    src={`http://localhost:5001/uploads/${currentPump.rightImage}`} 
                    alt="Right view of pump" 
                />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => handleNavigation(prevPump.id)}
                >
                    &#8592; {/* Left Arrow (back) */}
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump.id)}
                >
                    &#8594; {/* Right Arrow (forward) */}
                </button>
            </div>
        </div>
    );
};

export default PumpDetail;
