import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css'; // Import your custom CSS for styling

const PumpDetails = () => {
    const { pumpId } = useParams(); // Extract pumpId from the URL
    const navigate = useNavigate();

    const [pump, setPump] = useState(null); // State to hold pump data
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state

    // Fetch pump details from the API by ID
    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                // Fetch pump details based on the ID from the URL
                const response = await fetch(`http://localhost:5001/api/agpumps/${pumpId}`).the(res=res.json());
                conso
                console.log(pumpId);

                // If the response is not OK, throw an error
                if (!response.ok) {
                    throw new Error('Pump not found');
                }

                const data = await response.json();
                setPump(data.pump); // Set the pump data from the API response
                setLoading(false); // Set loading to false once the data is fetched
            } catch (err) {
                setError(err.message); // Handle error if fetch fails
                setLoading(false);
            }
        };

        fetchPumpDetails(); // Call the function to fetch pump details
    }, [pumpId]); // Re-run the effect when pumpId changes

    // Debugging logs
    console.log('Loading:', loading);
    console.log('Error:', error);
    console.log('Pump Data:', pump);

    if (loading) {
        return <div>Loading...</div>; // Show loading message while fetching data
    }

    if (error) {
        return <div>{error}. Please go back to the <a href="/products">products page</a>.</div>; // Show error message if pump not found
    }

    if (!pump) {
        return <div>Pump not found. Please go back to the <a href="/products">products page</a>.</div>; // Fallback if no pump data
    }

    return (
        <div className="body">
            {/* Product Description */}
            <div className="product-description">
                {/* Display the pump image */}
                <img 
                    src={`http://localhost:5001/uploads/${pump.image}`} 
                    alt={pump.name} 
                    className="product-image" 
                />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>Price:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                pump.features.map((feature, index) => (
                                    <li key={index}>{feature}</li>
                                ))
                            ) : (
                                <li>No features available.</li> // Fallback message if features are empty or undefined
                            )}
                        </ul>
                    </div>

                    {/* Send Enquiry Button */}
                    <a 
                        href="https://lallytradingcompany.onrender.com" 
                        className="buy-button"
                    >
                        Send Enquiry
                    </a>
                </div>
            </div>

            {/* Optional: Display additional pump details */}
            {pump.rightImage && (
                <div className="chart">
                    <h3>Technical Details</h3>
                    <img 
                        src={`http://localhost:5001/uploads/${pump.rightImage}`} 
                        alt="Right view of pump" 
                        className="product-image" 
                    />
                </div>
            )}

            {/* Navigation Button to go back */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => navigate('/products')} // Navigate back to product list
                >
                    &#8592; Back to Product List
                </button>
            </div>
        </div>
    );
};

export default PumpDetails;
