import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Home = () => {
  const [pumps, setPumps] = useState([]);
  const [selectedPump, setSelectedPump] = useState(null); // For displaying pump details
  const [error, setError] = useState('');

  // Fetch existing pumps from the backend
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  // Handle "Read More" button click
  const handleReadMore = (pump) => {
    setSelectedPump(pump); // Set the clicked pump's data to display details
  };

  // Close pump details view
  const handleCloseDetails = () => {
    setSelectedPump(null);
  };

  return (
    <div>
      <h1>Pump Gallery</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      {selectedPump ? (
        // Display detailed information about the selected pump
        <div className="pump-details">
          <button onClick={handleCloseDetails}>Back to Gallery</button>
          <h2>{selectedPump.name}</h2>
          <img src={`http://localhost:5001${selectedPump.image}`} alt={selectedPump.name} width="200" />
          <p><strong>Price:</strong> {selectedPump.price}</p>
          <p><strong>Features:</strong> {selectedPump.features.join(', ')}</p>
          <p><strong>Previous Pump ID:</strong> {selectedPump.previousId}</p>
          <p><strong>Next Pump ID:</strong> {selectedPump.nextId}</p>
        </div>
      ) : (
        // Display pump gallery
        <div className="pump-gallery">
          {pumps.length === 0 ? (
            <p>No pumps available.</p>
          ) : (
            pumps.map((pump) => (
              <div key={pump._id} className="pump-card">
                <img src={`http://localhost:5001${pump.image}`} alt={pump.name} width="100" />
                <h3>{pump.name}</h3>
                <p>{pump.price}</p>
                <button onClick={() => handleReadMore(pump)}>Read More</button>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default Home;
