import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/styl.css';

const Products = () => {
  const [agpumps, setAgpumps] = useState([]);
  const [residentialpumps, setResidentialpumps] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  // Fetch both agpumps and residential pumps
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        // Fetching agpumps data
        const agpumpsResponse = await fetch('http://localhost:5001/api/agpumps');
        const agpumpsData = await agpumpsResponse.json();
        setAgpumps(agpumpsData.agpumps);

        // Fetching residential pumps data
        const residentialpumpsResponse = await fetch('http://localhost:5001/api/residentialpumps');
        const residentialpumpsData = await residentialpumpsResponse.json();
        setResidentialpumps(residentialpumpsData.residentialpumps);

        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchPumps();
  }, []); // Empty dependency array ensures it runs once on component mount

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <div className="main-content" id="mainContent">
        <div className="product-info">
          <h2>Agricultural Pumps</h2>
          <div className="pump-gallery">
            {agpumps.map((pump, index) => (
              <div key={index} className="pump-item">
                <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">{pump.price}</p>
                  <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="product-info">
          <h2>Residential Pumps</h2>
          <div className="pump-gallery">
            {residentialpumps.map((pump, index) => (
              <div key={index} className="pump-item">
                <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">{pump.price}</p>
                  <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Products;
