import { motion } from 'framer-motion';

function PumpGallery({ pumps }) {
  const galleryVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        type: 'spring',
        stiffness: 50,
        damping: 10,
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, scale: 0.9 },
    visible: {
      opacity: 1,
      scale: 1,
      transition: { type: 'spring', stiffness: 100, damping: 10 },
    },
  };

  return (
    <motion.div
      className="pump-gallery"
      id="pumpGallery"
      variants={galleryVariants}
      initial="hidden"
      animate="visible"
    >
      {pumps.length > 0 ? (
        pumps.map((pump) => (
          <motion.div
            key={pump._id}
            className="pump-item"
            variants={itemVariants}
            whileHover={{ scale: 1.05 }} // Scale effect on hover
            whileTap={{ scale: 0.95 }}  // Tap effect
          >
            <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
            <div className="pump-info">
              <p className="pump-name">{pump.name}</p>
              <p className="pump-price">{pump.price}</p>
              <Link to={`/pumpDetail/${pump._id}`} className="read-more-btn">
                Read more
              </Link>
            </div>
          </motion.div>
        ))
      ) : (
        <p>Loading pumps...</p>
      )}
    </motion.div>
  );
}

export default PumpGallery;
