import React from 'react';
import { useParams } from 'react-router-dom';
import v4Image from '../images/v40.png'; // Import the actual image file
//import rightImageA from '../images/rightImageA.png'; // Import the right image

// Sample data for pumps
const pumps = [
    {
        id: 'v4',
        name: 'v4 stainless steel',
        features: ['Feature 1', 'Feature 2', 'Feature 3'],
        image: v4Image, // Use the imported image
        //rightImage: rightImageA // Use the imported right image
    },
    {
        id: '2',
        name: 'Pump B',
        features: ['Feature 1', 'Feature 2', 'Feature 3'],
        image: 'path/to/pumpB.jpg', // Replace with actual image path or import as needed
        //rightImage: 'path/to/rightImageB.jpg' // Replace with actual image path or import as needed
    }
];

const PumpDetail = () => {
    const { pumpId } = useParams(); // Get pumpId from URL parameters
    const pump = pumps.find(p => p.id === pumpId); // Find the pump by ID

    if (!pump) {
        return <div>Pump not found</div>; // Display message if pump is not found
    }

    return (
        <div style={{ padding: '20px' }}>
            <h1>{pump.name}</h1>
            <img src={pump.image} alt={pump.name} style={{ width: '300px', height: 'auto' }} />
            <h2>Features:</h2>
            <ul>
                {pump.features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                ))}
            </ul>
            <h2>Right Image:</h2>
            <img src={pump.rightImage} alt={`Right view of ${pump.name}`} style={{ width: '300px', height: 'auto' }} />
        </div>
    );
};

export default PumpDetail;
