import React, { useState } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pump, setPump] = useState({
    name: '',
    price: '',
    link: '',
    image: ''
  });
  const [error, setError] = useState('');

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setPump({
      ...pump,
      [name]: value
    });
  };

  // Handle image upload
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPump({
          ...pump,
          image: reader.result // Store the image data (Base64 or URL)
        });
      };
      reader.readAsDataURL(file); // Read image as Base64
    }
  };

  // Submit form data to the backend
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form inputs
    if (!pump.name || !pump.price || !pump.link || !pump.image) {
      setError('Please fill all fields and upload an image.');
      return;
    }

    try {
      // Send data to backend (Express server)
      const response = await axios.post('http://localhost:3001', pump);

      // Handle success
      console.log('Pump added:', response.data);
      setError(''); // Clear error message
      setPump({ name: '', price: '', link: '', image: '' }); // Reset form
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump. Please try again.');
    }
  };

  return (
    <div>
      <h1>Admin Portal - Add New Pump</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={pump.name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Price:</label>
          <input
            type="text"
            name="price"
            value={pump.price}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Link:</label>
          <input
            type="text"
            name="link"
            value={pump.link}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Image:</label>
          <input
            type="file"
            name="image"
            onChange={handleImageChange}
            required
          />
        </div>
        {error && <div style={{ color: 'red' }}>{error}</div>}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AdminPortal;
