import React from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Added useNavigate for programmatic navigation
import '../css/read.css'; 
import { pumps } from '../data/pumps';

const PumpDetail = () => {
    const { pumpId } = useParams();
    const navigate = useNavigate(); // To navigate to a different pump

    // Find the current pump based on the pumpId from URL
    const pump = pumps.find(p => p.id === pumpId);

    // If pump is not found, display a message
    if (!pump) {
        return <div>Pump not found</div>;
    }

    // Find the index of the current pump in the pumps array
    const currentIndex = pumps.findIndex(p => p.id === pumpId);

    // Get the next and previous pump in the array (circular navigation)
    const prevPump = pumps[(currentIndex - 1 + pumps.length) % pumps.length]; // Wrap around if at the start
    const nextPump = pumps[(currentIndex + 1) % pumps.length]; // Wrap around if at the end

    // Function to handle navigation
    const handleNavigation = (id) => {
        navigate(`/pumpDetail/${id}`);
    };

    return (
        <div className="body">

            <div className="product-description">
                <img src={pump.image} className="product-image" alt={pump.name} />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {pump.features.map((feature, index) => (
                                <li key={index}>{feature}</li>
                            ))}
                        </ul>
                    </div>

                    <a href="https://lallytradingcompany.onrender.com" className="buy-button">Send Enquiry</a>
                </div>
            </div>

            <div className="chart">
                <img src={pump.rightImage} alt="Right view of pump" />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <link></link>
                <button 
                    className="nav-btn left-btn"
                >
                    &#8592; {/* Left Arrow (back) */}
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump.id)}
                >
                    &#8594; {/* Right Arrow (forward) */}
                </button>
            </div>
        </div>
    );
};

export default PumpDetail;
