// components/PumpGallery.js
import React, { useState, useEffect } from 'react';
import './PumpGallery.css';

const PumpGallery = () => {
  const [pumps, setPumps] = useState([]);

  // Sample data array (could come from an API)
  const pumpData = [
    {
      id: 1,
      name: 'Residential Pump Model A',
      description: 'Efficient and reliable for residential use.',
      imageUrl: 'images/pump1.png',
    },
    {
      id: 2,
      name: 'Agricultural Pump Model B',
      description: 'High capacity for agricultural needs.',
      imageUrl: 'images/pump2.png',
    },
    {
      id: 3,
      name: 'Industrial Pump Model C',
      description: 'Built for industrial operations.',
      imageUrl: 'images/pump3.png',
    },
    // Add more items as needed
  ];

  useEffect(() => {
    // Simulating data fetching
    setTimeout(() => {
      setPumps(pumpData); // Sets the pump data after "fetching"
    }, 500); // Delay added to mimic data fetching
  }, []);

  return (
    <div className="pump-gallery">
      {pumps.length > 0 ? (
        pumps.map((pump) => (
          <div key={pump.id} className="pump-item">
            <img src={pump.imageUrl} alt={pump.name} className="pump-image" />
            <h3 className="pump-name">{pump.name}</h3>
            <p className="pump-description">{pump.description}</p>
          </div>
        ))
      ) : (
        <p>Loading pumps...</p>
      )}
    </div>
  );
};

export default PumpGallery;
