import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import bg1 from '../images/PUMP.jpg';
import '../css/styles.css';

function Home() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [pumps, setPumps] = useState([]);

  const images = [bg1];
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [images.length]);

  useEffect(() => {
    fetch('http://localhost:5001/api/pumps')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to fetch data');
        }
        return response.json();
      })
      .then((data) => {
        setPumps(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching pumps:', error);
        setError('Failed to load pumps.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="main-content">
      <div className="image-section1">
        <div className="slider">
          <img src={images[currentIndex]} alt="Main Display" className="slider-image" />
        </div>
      </div>

      <h2 className="section-title">Residential Submersible Pumps</h2>
      <div className="pump-gallery">
        {loading ? (
          <div>Loading pumps...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          pumps.length > 0 ? (
            pumps.map((pump) => (
              <div key={pump.id} className="pump-item">
                <img 
                  src={`http://localhost:5001/uploads/${pump.image}`} 
                  alt={pump.name} 
                  className="pump-image" 
                />
                <div className="pump-info">
                  <h3 className="pump-name">{pump.name}</h3>
                  <p className="pump-price">{pump.price}</p>
                  {pump.features && (
                    <ul className="pump-features">
                      {pump.features.map((feature, index) => (
                        <li key={index}>{feature}</li>
                      ))}
                    </ul>
                  )}
                  <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
                </div>
                {pump.rightImage && (
                  <img 
                    src={`http://localhost:5001/uploads/${pump.rightImage}`} 
                    alt={`${pump.name} - Side View`} 
                    className="pump-right-image" 
                  />
                )}
              </div>
            ))
          ) : (
            <div>No pumps available.</div>
          )
        )}
      </div>
    </div>
  );
}

export default Home;
