import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css';  // Using the same CSS
import axios from 'axios';

const AgPumpDetail = () => {
    const { pumpId } = useParams();
    const navigate = useNavigate();

    const [pump, setPump] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [pumps, setPumps] = useState([]);

    // Fetch all agricultural pumps
    useEffect(() => {
        axios.get('http://localhost:5001/api/agpumps')
            .then(response => setPumps(response.data))
            .catch(error => console.error('Error fetching agricultural pumps:', error));
    }, []);

    // Fetch specific agricultural pump details
    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                console.log('Fetching details for agricultural pump ID:', pumpId);

                const response = await fetch(`http://localhost:5001/api/agpumps/${pumpId}`);

                if (!response.ok) {
                    throw new Error('Agricultural pump not found');
                }

                const data = await response.json();
                console.log('Fetched agricultural pump data:', data);

                setPump(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchPumpDetails();
    }, [pumpId]);

    // Debugging logs
    console.log('Loading:', loading);
    console.log('Error:', error);
    console.log('Agricultural Pump Data:', pump);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}. Please go back to the <a href="/home">Home page</a>.</div>;
    }

    if (!pump) {
        return <div>Agricultural pump not found. Please go back to the <a href="/home">Home page</a>.</div>;
    }

    // Create array of pump IDs for navigation
    const pumpIds = pumps.map(pump => pump.id);
    const currentIndex = pumpIds.indexOf(pumpId);

    // Navigation handler
    const handleNavigation = (id) => {
        navigate(`/agPumpDetail/${id}`);  // Updated route for agricultural pumps
    };

    // Get previous and next pump IDs with wraparound
    const prevPump = pumpIds[currentIndex - 1] || pumpIds[pumpIds.length - 1];
    const nextPump = pumpIds[currentIndex + 1] || pumpIds[0];

    return (
        <div className="body">
            {/* Product Description */}
            <div className="product-description">
                <img 
                    src={`http://localhost:5001${pump.image}`}  // Updated image path handling
                    className="product-image" 
                    alt={pump.name} 
                    onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.png';  // Add a placeholder image
                    }}
                />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                pump.features.map((feature, index) => (
                                    <li key={index}>{feature}</li>
                                ))
                            ) : (
                                <li>No features available.</li>
                            )}
                        </ul>
                    </div>

                    <a 
                        href="https://lallytradingcompany.onrender.com" 
                        className="buy-button"
                    >
                        Send Enquiry
                    </a>
                </div>
            </div>

            {/* Right view image */}
            <div className="chart">
                <img 
                    src={`http://localhost:5001${pump.rightImage}`}  // Updated image path handling
                    alt="Right view of agricultural pump"
                    onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.png';  // Add a placeholder image
                    }}
                />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => handleNavigation(prevPump)}
                >
                    &#8592;
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump)}
                >
                    &#8594;
                </button>
            </div>
        </div>
    );
};

export default AgPumpDetail;
