import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ name: '', price: '', link: '' });
  const [image, setImage] = useState(null);

  useEffect(() => {
    // Fetch existing pumps
    const fetchPumps = async () => {
      const response = await axios.get('http://localhost:5001/api/pumps');
      setPumps(response.data);
    };
    fetchPumps();
  }, []);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('image', file);

    const response = await axios.post('http://localhost:5000/api/upload', formData);
    setImage(response.data.imageUrl);
  };

  const handleAddPump = async () => {
    const pump = { ...newPump, image };
    const response = await axios.post('http://localhost:5000/api/pumps', pump);
    setPumps([...pumps, response.data]);
    setNewPump({ name: '', price: '', link: '' });
    setImage(null);
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {/* Add New Pump Form */}
      <div>
        <h2>Add New Pump</h2>
        <input
          type="text"
          placeholder="Name"
          value={newPump.name}
          onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Price"
          value={newPump.price}
          onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Link"
          value={newPump.link}
          onChange={(e) => setNewPump({ ...newPump, link: e.target.value })}
        />
        <input type="file" onChange={handleImageUpload} />
        <button onClick={handleAddPump}>Add Pump</button>
      </div>

      {/* Existing Pumps */}
      <div>
        <h2>Existing Pumps</h2>
        {pumps.map((pump) => (
          <div key={pump._id}>
            <img src={`http://localhost:5000${pump.image}`} alt={pump.name} width="100" />
            <p>{pump.name}</p>
            <p>{pump.price}</p>
            <p>{pump.link}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPortal;
