import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ name: '', price: '', features: '', previousId: '', nextId: '' });
  const [image, setImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [error, setError] = useState('');

  // Fetch existing pumps from the backend
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const files = e.target.files;
    const formData = new FormData();

    if (files.length > 0) {
      formData.append('image', files[0]);
      formData.append('rightImage', files[1]);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.imagePath);
        setRightImage(response.data.rightImagePath);
      } catch (err) {
        console.error('Error uploading images:', err);
        setError('Error uploading images.');
      }
    }
  };

  // Handle adding a new pump
  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !newPump.features || !image || !rightImage) {
      setError('Please fill all fields and upload images.');
      return;
    }

    try {
      const pump = { ...newPump, image, rightImage };
      const response = await axios.post('http://localhost:5001/api/pumps', pump);
      setPumps([...pumps, response.data.pump]);
      setNewPump({ name: '', price: '', features: '', previousId: '', nextId: '' });
      setImage(null);
      setRightImage(null);
      setError('');
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump.');
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <h2>Add New Pump</h2>
      <input
        type="text"
        placeholder="Name"
        value={newPump.name}
        onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Price"
        value={newPump.price}
        onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
      />
      <input
        type="text"
        placeholder="Features (comma separated)"
        value={newPump.features}
        onChange={(e) => setNewPump({ ...newPump, features: e.target.value })}
      />
      <input
        type="text"
        placeholder="Previous ID"
        value={newPump.previousId}
        onChange={(e) => setNewPump({ ...newPump, previousId: e.target.value })}
      />
      <input
        type="text"
        placeholder="Next ID"
        value={newPump.nextId}
        onChange={(e) => setNewPump({ ...newPump, nextId: e.target.value })}
      />
      <input type="file" onChange={handleImageUpload} multiple />
      <button onClick={handleAddPump}>Add Pump</button>

      <h2>Existing Pumps</h2>
      {pumps.length === 0 ? (
        <p>No pumps available.</p>
      ) : (
        pumps.map((pump) => (
          <div key={pump._id}>
            <img src={`http://localhost:5001${pump.image}`} alt={pump.name} width="100" />
            <p>{pump.name}</p>
            <p>{pump.price}</p>
            <p>{pump.features.join(', ')}</p>
            <button>Read More</button>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminPortal;
