import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/styles.css';

function Home() {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetch('http://localhost:5001/api/pumps')
      .then((response) => response.json())
      .then((data) => {
        if (data.success && data.pumps.length > 0) {
          setPumps(data.pumps); // Update pumps state
        } else {
          setError('No pumps available.');
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching pumps:', error);
        setError('Failed to load pumps.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="main-content">
      <h2 className="section-title">Residential Submersible Pumps</h2>
      <div className="pump-gallery">
        {loading ? (
          <div>Loading pumps...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          pumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <img
                src={`http://localhost:5001/uploads/${pump.image}`}
                alt={pump.name}
                className="pump-image"
              />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump._id}`} className="read-more-btn">
                  Read more
                </Link>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Home;
