import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);

  // Fetch pumps data from the backend
  const fetchPumps = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/pumps');
      setPumps(response.data); // Set fetched pumps to the state
    } catch (error) {
      console.error('Error fetching pumps:', error);
    }
  };

  useEffect(() => {
    fetchPumps(); // Call the function when the component mounts
  }, []); // Empty dependency array means this runs only once after the first render

  return (
    <div>
      <h1>Admin Portal</h1>
      <h2>Pump List</h2>
      <ul>
        {pumps.map((pump) => (
          <li key={pump._id}>
            <h3>{pump.name}</h3>
            <p>{pump.price}</p>
            <img src={pump.image} alt={pump.name} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPortal;
