import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/styles.css';

function Home() {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const images = [require('../images/PUMP.jpg')]; // Main image for the slider
  const [currentIndex, setCurrentIndex] = useState(0);

  // Automatic sliding for the main image
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [images.length]);

  // Fetch pumps data from the backend API
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/pumps');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        console.log('Fetched pump data: ', data); // Log to check data
        setPumps(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError(`Failed to load pumps data: ${error.message}`);
        setLoading(false);
      }
    };
    fetchPumps();
  }, []);

  return (
    <div className="main-content">
      {/* Main Image Slider */}
      <div className="image-section1 animate-on-scroll">
        <div className="slider">
          <img src={images[currentIndex]} alt="Main Display" className="slider-image" />
        </div>
      </div>

      {/* Description Section */}
      <div className="b animate-on-scroll">
        <center>
          <p className="b2">
            Explore our range of Agricultural submersible pumps <br />
            The product range covers a wide application such as agriculture,
            water supply to townships, Industries, sewage, and drainage.
          </p>
          <Link to="/products" className="btn">View Products</Link>
        </center>
      </div>

      {/* Pumps Section */}
      <h2 className="section-title animate-on-scroll">Residential Submersible Pump</h2>
      {loading ? (
        <div>Loading pumps...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <div className="pump-gallery" id="pumpGallery">
          {pumps.length > 0 ? (
            pumps.map((pump) => (
              <div key={pump._id} className="pump-item animate-on-scroll">
                <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">{`MRP ₹${pump.price}`}</p>
                  <Link to={`/pumpDetail/${pump._id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))
          ) : (
            <div>No pumps available.</div>
          )}
        </div>
      )}
    </div>
  );
}

export default Home;
