// AdminPortal.js (React)

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ id: '', name: '', price: '', features: '', previousId: '', nextId: '' });
  const [image, setImage] = useState(null);
  const [rightImage, setRightImage] = useState(null);
  const [error, setError] = useState('');

  // Fetch all pumps for the gallery
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  // Handle image uploads and form submission
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('image', file);
      formData.append('rightImage', file);  // Assuming you're uploading both images here
      axios.post('http://localhost:5001/api/pumps', formData)
        .then((res) => {
          console.log('Upload successful');
        })
        .catch((err) => {
          setError('Error uploading images');
          console.error(err);
        });
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <h2>Add New Pump</h2>
      <input
        type="text"
        placeholder="ID"
        value={newPump.id}
        onChange={(e) => setNewPump({ ...newPump, id: e.target.value })}
      />
      <input
        type="text"
        placeholder="Name"
        value={newPump.name}
        onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Price"
        value={newPump.price}
        onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
      />
      <input
        type="text"
        placeholder="Features"
        value={newPump.features}
        onChange={(e) => setNewPump({ ...newPump, features: e.target.value })}
      />
      <input
        type="text"
        placeholder="Previous Pump ID"
        value={newPump.previousId}
        onChange={(e) => setNewPump({ ...newPump, previousId: e.target.value })}
      />
      <input
        type="text"
        placeholder="Next Pump ID"
        value={newPump.nextId}
        onChange={(e) => setNewPump({ ...newPump, nextId: e.target.value })}
      />
      <input type="file" onChange={handleImageUpload} />
      <button>Submit Pump</button>

      <h2>Pump Gallery</h2>
      <div>
        {pumps.map((pump) => (
          <div key={pump.id}>
            <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} width="100" />
            <h3>{pump.name}</h3>
            <p>{pump.price}</p>
            <button onClick={() => alert(`Read more about ${pump.name}`)}>Read More</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPortal;
