import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ name: '', price: '', features: '' });
  const [image, setImage] = useState(null);
  const [error, setError] = useState('');

  // Fetch pumps
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data.pumps);
      } catch (err) {
        console.error('Error fetching pumps:', err);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('image', file);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.imageUrl);
      } catch (err) {
        console.error('Error uploading image:', err);
        setError('Failed to upload image.');
      }
    }
  };

  // Add a pump
  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !newPump.features || !image) {
      setError('Please fill all fields and upload an image.');
      return;
    }

    try {
      const featuresArray = newPump.features.split(',').map(f => f.trim());
      const pump = { ...newPump, features: featuresArray, image };
      const response = await axios.post('http://localhost:5001/api/pumps', pump);
      setPumps([...pumps, response.data]);
      setNewPump({ name: '', price: '', features: '' });
      setImage(null);
      setError('');
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Failed to add pump.');
    }
  };

  // Remove a pump
  const handleRemovePump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      setPumps(pumps.filter(pump => pump._id !== id));
    } catch (err) {
      console.error('Error removing pump:', err);
      setError('Failed to remove pump.');
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      <h2>Add New Pump</h2>
      <input
        type="text"
        placeholder="Name"
        value={newPump.name}
        onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Price"
        value={newPump.price}
        onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
      />
      <input
        type="text"
        placeholder="Features (comma-separated)"
        value={newPump.features}
        onChange={(e) => setNewPump({ ...newPump, features: e.target.value })}
      />
      <input type="file" onChange={handleImageUpload} />
      <button onClick={handleAddPump}>Add Pump</button>

      <h2>Existing Pumps</h2>
      {pumps.length === 0 ? (
        <p>No pumps available.</p>
      ) : (
        pumps.map(pump => (
          <div key={pump._id}>
            <img src={`http://localhost:5001${pump.image}`} alt={pump.name} width="100" />
            <p>{pump.name}</p>
            <p>{pump.price}</p>
            <ul>
              {pump.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
            <button onClick={() => handleRemovePump(pump._id)}>Remove</button>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminPortal;
