import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ name: '', price: '', link: '' });
  const [image, setImage] = useState(null);

  // Fetch existing pumps from the server
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data);
      } catch (error) {
        console.error('Error fetching pumps:', error);
      }
    };
    fetchPumps();
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('http://localhost:5001/api/upload', formData);
      setImage(response.data.imageUrl);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  // Handle adding new pump
  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !newPump.link || !image) {
      alert("Please fill all fields and upload an image.");
      return;
    }

    const pump = { ...newPump, image };
    try {
      const response = await axios.post('http://localhost:5001/api/pumps', pump);
      setPumps([...pumps, response.data]);
      setNewPump({ name: '', price: '', link: '' });
      setImage(null);
    } catch (error) {
      console.error('Error adding pump:', error);
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {/* Add New Pump Form */}
      <div>
        <h2>Add New Pump</h2>
        <input
          type="text"
          placeholder="Name"
          value={newPump.name}
          onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Price"
          value={newPump.price}
          onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
        />
        <input
          type="text"
          placeholder="Link"
          value={newPump.link}
          onChange={(e) => setNewPump({ ...newPump, link: e.target.value })}
        />
        <input type="file" onChange={handleImageUpload} />
        <button onClick={handleAddPump}>Add Pump</button>
      </div>

      {/* Existing Pumps */}
      <div>
        <h2>Existing Pumps</h2>
        {pumps.map((pump) => (
          <div key={pump._id}>
            <img src={`http://localhost:5001${pump.image}`} alt={pump.name} width="100" />
            <p>{pump.name}</p>
            <p>{pump.price}</p>
            <p>{pump.link}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AdminPortal;
