import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css'; // Include your custom styles

const Home = () => {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch pumps data from the backend
  useEffect(() => {
    fetch('http://localhost:5001/api/pumps')
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setPumps(data.pumps);
        } else {
          setError('Failed to load pumps.');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching pumps:', err);
        setError('Failed to load pumps.');
        setLoading(false);
      });
  }, []);

  return (
    <div className="home">
      <h1>Welcome to Lally Trading Company</h1>
      <p>Providing high-quality pumps for residential and agricultural needs for over 25 years.</p>

      <section className="gallery-section">
        <h2>Our Products</h2>

        {loading ? (
          <p>Loading pumps...</p>
        ) : error ? (
          <p className="error-message">{error}</p>
        ) : pumps.length === 0 ? (
          <p>No pumps available at the moment. Please check back later.</p>
        ) : (
          <div className="gallery">
            {pumps.map((pump) => (
              <div key={pump.id} className="gallery-item">
                <img 
                  src={`http://localhost:5001/uploads/${pump.image}`} 
                  alt={pump.name} 
                  className="pump-image" 
                />
                <h3>{pump.name}</h3>
                <p>{pump.price}</p>
                <Link to={`/pumpDetail/${pump.id}`} className="details-button">
                  View Details
                </Link>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Home;
