import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Import Axios
import '../css/styl.css';

const Products = () => {
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [selectedImage, setSelectedImage] = useState(null); // State to manage image click

  useEffect(() => {
    // Fetch agricultural pumps
    axios
      .get('http://localhost:5001/api/agpumps')
      .then((response) => setAgriculturalPumps(response.data))
      .catch((error) => console.error('Error fetching agricultural pumps:', error));
  }, []);

  const handleScroll = () => {
    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach((el) => {
      const rect = el.getBoundingClientRect();
      if (rect.top < window.innerHeight - 100) {
        el.classList.add('slide-in');
      }
    });
  };

  // Adding scroll event listener and cleanup
  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    // Cleanup on component unmount
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // Function to handle image click
  const handleImageClick = (image) => {
    setSelectedImage(image); // Set the clicked image as selected
  };

  // Function to close the larger image view
  const closeImageView = () => {
    setSelectedImage(null); // Clear the selected image
  };

  return (
    <div>
      <div className="main-content" id="mainContent">
        <div className="product-info">
          <div className="container1">
            <div>
              <h2>Agricultural Products</h2>
            </div>
            <div>
              <p>We offer a wide range of Agricultural submersible pumps to suit your needs.</p>
            </div>
          </div>
        </div>

        {/* Agricultural Pumps Section */}
        <div className="pump-gallery">
          {agriculturalPumps.length > 0 ? (
            agriculturalPumps.map((pump) => (
              <div
                key={pump.id}
                className="pump-item animate-on-scroll"
                onClick={() => handleImageClick(pump.image)} // Add click handler
              >
                {/* Add 3D effect */}
                <div className="image-wrapper">
                  <img
                    src={`http://localhost:5001/uploads/${pump.image}`}
                    alt={pump.name}
                    className="pump-image"
                  />
                </div>
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">â‚¹{pump.price}</p>
                  <Link to={`/pumpDetails/${pump.id}`} className="read-more-btn">
                    Read more
                  </Link>
                </div>
              </div>
            ))
          ) : (
            <p>Loading Agricultural Pumps...</p>
          )}
        </div>
      </div>

      {/* Larger Image View */}
      {selectedImage && (
        <div className="image-viewer" onClick={closeImageView}>
          <div className="image-viewer-content">
            <img
              src={`http://localhost:5001/uploads/${selectedImage}`}
              alt="Selected Pump"
              className="selected-image"
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default Products;
