import React, { useEffect, useState } from "react";
import axios from "axios";

const Enquiries = () => {
  const [enquiries, setEnquiries] = useState([]);
  const [readStatus, setReadStatus] = useState(() => {
    const savedStatus = localStorage.getItem("readStatus");
    return savedStatus ? JSON.parse(savedStatus) : {};
  });

  useEffect(() => {
    axios
      .get("http://localhost:5001/api/enquiries")
      .then((response) => {
        setEnquiries(response.data);
      })
      .catch((error) => console.error("Error fetching enquiries:", error));
  }, []);

  const toggleReadStatus = (id) => {
    const updatedStatus = { ...readStatus, [id]: !readStatus[id] };
    setReadStatus(updatedStatus);
    localStorage.setItem("readStatus", JSON.stringify(updatedStatus));
  };

  const handleDeleteEnquiry = async (id) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this enquiry?');

    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:5001/api/enquiries/${id}`);
        setEnquiries(prev => prev.filter(enquiry => enquiry._id !== id));
        alert('Enquiry deleted successfully!');
      } catch (error) {
        console.error('Error deleting enquiry:', error);
        setError('Error deleting enquiry.');
      }
    } else {
      console.log('Enquiry deletion was canceled.');
    }
  };

  const styles = {
    container: {
      display: "grid",
      gridTemplateColumns: "repeat(4, 1fr)",
      gap: "20px",
      padding: "20px",
      backgroundColor: "#f5f5f5",
    },
    card: {
      backgroundColor: "#fff",
      padding: "20px",
      borderRadius: "8px",
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
      textAlign: "left",
    },
    checkboxContainer: {
      marginTop: "10px",
    },
    deleteButton: {
      backgroundColor: "#ff4d4d",
      color: "#fff",
      border: "none",
      padding: "5px 10px",
      cursor: "pointer",
      marginLeft: "10px",
      borderRadius: "4px",
    },
    // Media queries for responsiveness
    "@media (max-width: 1024px)": {
      container: {
        gridTemplateColumns: "repeat(2, 1fr)",
      },
    },
    "@media (max-width: 600px)": {
      container: {
        gridTemplateColumns: "1fr",
      },
    },
  };

  return (
    <div style={styles.container}>

    </div>
  );
};

export default Enquiries;
