import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PumpDetails = () => {
  const { id } = useParams();
  const [pump, setPump] = useState(null);

  // Fetch pump details by ID
  useEffect(() => {
    const fetchPumpDetails = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/pumps/${id}`);
        setPump(response.data);
      } catch (error) {
        console.error('Error fetching pump details:', error);
      }
    };

    fetchPumpDetails();
  }, [id]);

  if (!pump) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{pump.name}</h1>
      <img src={pump.image} alt={pump.name} style={{ width: '300px' }} />
      <p><strong>Price:</strong> {pump.price}</p>
      <h3>Features</h3>
      <ul>
        {pump.features.map((feature, index) => (
          <li key={index}>{feature}</li>
        ))}
      </ul>
      <img src={pump.rightImage} alt="Pump Table" style={{ width: '300px' }} />
    </div>
  );
};

export default PumpDetails;
