import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [currentView, setCurrentView] = useState(null); // Default: null (no pumps displayed)
  const [showAddForm, setShowAddForm] = useState(false); // Toggle Add Pump form
  const [newPump, setNewPump] = useState({ name: '', price: '', image: null }); // New pump data
  const [error, setError] = useState(null);

  // Fetch residential pumps data from backend
  useEffect(() => {
    axios.get('http://localhost:5001/api/pumps')
      .then(response => setResidentialPumps(response.data))
      .catch(error => console.error('Error fetching residential pumps:', error));
  }, []);

  // Fetch agricultural pumps data from backend
  useEffect(() => {
    axios.get('http://localhost:5001/api/agpumps')
      .then(response => setAgriculturalPumps(response.data))
      .catch(error => console.error('Error fetching agricultural pumps:', error));
  }, []);

  // Add a new pump
  const handleAddPump = async () => {
    const formData = new FormData();
    formData.append('name', newPump.name);
    formData.append('price', newPump.price);
    formData.append('image', newPump.image);

    try {
      const response = await axios.post(
        `http://localhost:5001/api/${currentView === 'residential' ? 'pumps' : 'agpumps'}`,
        formData
      );
      if (currentView === 'residential') {
        setResidentialPumps([...residentialPumps, response.data]);
      } else {
        setAgriculturalPumps([...agriculturalPumps, response.data]);
      }
      setShowAddForm(false);
      setNewPump({ name: '', price: '', image: null });
    } catch (error) {
      setError('Error adding pump');
      console.error(error);
    }
  };

  // Delete a pump
  const handleDeletePump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      setResidentialPumps(residentialPumps.filter(pump => pump._id !== id));
      setAgriculturalPumps(agriculturalPumps.filter(pump => pump._id !== id));
    } catch (error) {
      setError('Error deleting pump');
      console.error(error);
    }
  };

  // Handle new pump inputs
  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === 'image') {
      setNewPump({ ...newPump, image: files[0] });
    } else {
      setNewPump({ ...newPump, [name]: value });
    }
  };

  const pumpsToDisplay = currentView === 'residential' ? residentialPumps : agriculturalPumps;

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>

      {/* Sidebar */}
      <div className="sidebar">
        <button
          className={`sidebar-btn ${currentView === 'residential' ? 'active' : ''}`}
          onClick={() => setCurrentView('residential')}
        >
          Residential Pumps
        </button>
        <button
          className={`sidebar-btn ${currentView === 'agricultural' ? 'active' : ''}`}
          onClick={() => setCurrentView('agricultural')}
        >
          Agricultural Pumps
        </button>
      </div>

      {/* Conditional Rendering Based on View */}
      {currentView && (
        <div className="pump-gallery">
          {pumpsToDisplay.length > 0 ? (
            pumpsToDisplay.map((pump) => (
              <div key={pump._id} className="pump-item">
                {/* Three Dots Menu */}
                <div className="dots-menu">
                  <span onClick={() => handleDeletePump(pump._id)}>Delete</span>
                </div>
                <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">{pump.price}</p>
                  <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))
          ) : (
            <p>No pumps available</p>
          )}

          {/* Empty Catalog for Adding New Pump */}
          <div className="pump-item empty" onClick={() => setShowAddForm(true)}>
            <div className="add-icon">+</div>
          </div>
        </div>
      )}

      {/* Add Pump Form */}
      {showAddForm && (
        <div className="add-form">
          <h3>Add New Pump</h3>
          {error && <p className="error">{error}</p>}
          <input
            type="text"
            name="name"
            placeholder="Pump Name"
            value={newPump.name}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="price"
            placeholder="Pump Price"
            value={newPump.price}
            onChange={handleInputChange}
          />
          <input
            type="file"
            name="image"
            onChange={handleInputChange}
          />
          <button onClick={handleAddPump}>Add Pump</button>
          <button onClick={() => setShowAddForm(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
};

export default AdminPortal;
