import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Grid, TextField, Button, Typography, Card, CardContent, CardMedia, Box } from '@mui/material';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]);
  const [newPump, setNewPump] = useState({ name: '', price: '' });
  const [image, setImage] = useState(null);

  // Fetch existing pumps
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data);
      } catch (error) {
        console.error('Error fetching pumps:', error);
      }
    };
    fetchPumps();
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('http://localhost:5001/api/upload', formData);
      setImage(response.data.imageUrl);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  // Handle adding new pump
  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !newPump.link || !image) {
      alert("Please fill all fields and upload an image.");
      return;
    }

    const pump = { ...newPump, image };
    try {
      const response = await axios.post('http://localhost:5001/api/pumps', pump);
      setPumps([...pumps, response.data]);
      setNewPump({ name: '', price: '' });
      setImage(null);
    } catch (error) {
      console.error('Error adding pump:', error);
    }
  };

  return (
    <Container maxWidth="lg" sx={{ paddingTop: 3 }}>
      <Typography variant="h4" gutterBottom>Admin Portal</Typography>

      {/* Add New Pump Form */}
      <Box component="form" sx={{ marginBottom: 3 }}>
        <Typography variant="h6" gutterBottom>Add New Pump</Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Pump Name"
              value={newPump.name}
              onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Price"
              value={newPump.price}
              onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <Button variant="contained" component="label" fullWidth>
              Upload Image
              <input
                type="file"
                hidden
                onChange={handleImageUpload}
              />
            </Button>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Button variant="contained" fullWidth onClick={handleAddPump}>
              Add Pump
            </Button>
          </Grid>
        </Grid>
      </Box>

      {/* Show Image Preview if exists */}
      {image && (
        <Box sx={{ marginBottom: 3 }}>
          <Typography variant="body1">Image Preview:</Typography>
          <img src={`http://localhost:5001${image}`} alt="Uploaded" width="200" />
        </Box>
      )}

      {/* Display Existing Pumps */}
      <Typography variant="h6" gutterBottom>Existing Pumps</Typography>
      <Grid container spacing={2}>
        {pumps.map((pump) => (
          <Grid item xs={12} sm={4} key={pump._id}>
            <Card>
              <CardMedia
                component="img"
                height="140"
                image={`http://localhost:5001${pump.image}`}
                alt={pump.name}
              />
              <CardContent>
                <Typography variant="h6">{pump.name}</Typography>
                <Typography variant="body2">{pump.price}</Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default AdminPortal;
