import React, { useContext } from 'react';
import { PumpContext } from '../context/PumpContext'; // Import the PumpContext

const Home = () => {
    const { pumps, loading, error } = useContext(PumpContext); // Access the pump data

    if (loading) {
        return <div>Loading pumps...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Pump Gallery</h2>
            <div className="pump-gallery">
                {pumps.map((pump) => (
                    <div key={pump._id} className="pump-item">
                        <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                        <h3>{pump.name}</h3>
                        <p>{pump.price}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
