import React from 'react';
import { useParams } from 'react-router-dom';
import pumps from '../data/pumps';

const PumpDetail = () => {
    const { pumpId } = useParams(); // Get pumpId from URL
    const pump = pumps.find(p => p.id === String(pumpId)); // Convert pumpId to string

    // Error handling for undefined pump
    if (!pump) {
        return <h2>Pump not found!</h2>;
    }

    return (
        <div>
            <h1>{pump.name}</h1>
            <img src={pump.image} alt={pump.name} />
            <h2>Features</h2>
            <ul>
                {pump.features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                ))}
            </ul>
            <img src={pump.rightImage} alt={`Right view of ${pump.name}`} />
        </div>
    );
};

export default PumpDetail;
