import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css'; 

const PumpDetail = () => {
    const { pumpId } = useParams(); // Extract the pumpId from the URL
    const navigate = useNavigate();

    const [pump, setPump] = useState(null); // State to store the pump details
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(''); // Error state

    useEffect(() => {
        // Fetch pump details by ID
        fetch(`http://localhost:5001/api/pumps/${pumpId}`)
            .then((response) => response.json())
            .then((data) => {
                if (data.success) {
                    setPump(data.pump); // Set the pump data
                } else {
                    setError('Pump not found.');
                }
                setLoading(false);
            })
            .catch((err) => {
                console.error('Error fetching pump details:', err);
                setError('Failed to fetch pump details.');
                setLoading(false);
            });
    }, [pumpId]);

    const handleNavigation = (id) => {
        navigate(`/pumpDetail/${id}`); // Navigate to the next or previous pump
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    // Determine prev and next IDs for navigation
    const prevId = pump.previousId;
    const nextId = pump.nextId;

    return (
        <div className="body">

            {/* Product Description */}
            <div className="product-description">
                <img src={`http://localhost:5001/uploads/${pump.image}`} className="product-image" alt={pump.name} />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {pump.features.map((feature, index) => (
                                <li key={index}>{feature}</li>
                            ))}
                        </ul>
                    </div>

                    <a href="https://lallytradingcompany.onrender.com" className="buy-button">Send Enquiry</a>
                </div>
            </div>

            {/* Image for right view of the pump */}
            <div className="chart">
                <img src={`http://localhost:5001/uploads/${pump.rightImage}`} alt="Right view of pump" />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                {prevId && (
                    <button 
                        className="nav-btn left-btn"
                        onClick={() => handleNavigation(prevId)}
                    >
                        &#8592; {/* Left Arrow (back) */}
                    </button>
                )}
                {nextId && (
                    <button 
                        className="nav-btn right-btn"
                        onClick={() => handleNavigation(nextId)}
                    >
                        &#8594; {/* Right Arrow (forward) */}
                    </button>
                )}
            </div>
        </div>
    );
};

export default PumpDetail;
