import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/Admin.css';

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [error, setError] = useState(null);
  const [currentView, setCurrentView] = useState('residential');
  const [showAddForm, setShowAddForm] = useState(false);
  const [pumpType, setPumpType] = useState('residential');
  const [newPumpData, setNewPumpData] = useState({
    id: '',
    name: '',
    price: '',
    features: '',
    image: null,
    rightImage: null
  });

  const [enquiries, setEnquiries] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showEnquiries, setShowEnquiries] = useState(false); // New state for managing Enquiries view

  // Fetch residential pumps data
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/pumps')
      .then((response) => setResidentialPumps(response.data))
      .catch((error) => console.error('Error fetching residential pumps:', error));
  }, []);

  // Fetch agricultural pumps data
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/agpumps')
      .then((response) => setAgriculturalPumps(response.data))
      .catch((error) => console.error('Error fetching agricultural pumps:', error));
  }, []);

  // Fetch enquiries from the backend
  const fetchEnquiries = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/enquiries');
      setEnquiries(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching enquiries:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    if (showEnquiries) {
      fetchEnquiries();
    }
  }, [showEnquiries]);

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>

      <div className="sidebar">
        <button
          className={`sidebar-btn ${currentView === 'residential' ? 'active' : ''}`}
          onClick={() => setCurrentView('residential')}
        >
          Residential Pumps
        </button>
        <button
          className={`sidebar-btn ${currentView === 'agricultural' ? 'active' : ''}`}
          onClick={() => setCurrentView('agricultural')}
        >
          Agricultural Pumps
        </button>
        <button
          className={`sidebar-btn ${showEnquiries ? 'active' : ''}`}
          onClick={() => setShowEnquiries(true)}
        >
          Enquiries
        </button>
      </div>

      {showEnquiries ? (
        <div className="enquiries-container">
          <h2>Enquiries</h2>
          {loading ? (
            <p>Loading enquiries...</p>
          ) : (
            <ul>
              {enquiries.map((enquiry) => (
                <li key={enquiry.id}>
                  <p><strong>{enquiry.name}</strong></p>
                  <p>{enquiry.email}</p>
                  <p>{enquiry.message}</p>
                </li>
              ))}
            </ul>
          )}
        </div>
      ) : (
        <>
          {/* Show residential or agricultural pumps based on current view */}
          <div className="gallery-container">
            <div className="add-new-pump" onClick={() => setShowAddForm(true)}>
              <span className="plus-sign">+</span>
              <p className="add-text">Add New Pump</p>
            </div>
          </div>

          {showAddForm && (
            <div className="add-pump-form">
              <h2>Add New Pump</h2>
              {error && <p className="error">{error}</p>}
              <form onSubmit={handleAddPump}>
                <select
                  name="pumpType"
                  value={pumpType}
                  onChange={handlePumpTypeChange}
                  className="pump-type-select"
                  required
                >
                  <option value="residential">Residential Pump</option>
                  <option value="agricultural">Agricultural Pump</option>
                </select>

                <input
                  type="text"
                  name="id"
                  value={newPumpData.id}
                  onChange={handleInputChange}
                  placeholder="Pump ID"
                  required
                />
                <input
                  type="text"
                  name="name"
                  value={newPumpData.name}
                  onChange={handleInputChange}
                  placeholder="Pump Name"
                  required
                />
                <input
                  type="text"
                  name="price"
                  value={newPumpData.price}
                  onChange={handleInputChange}
                  placeholder="Pump Price"
                  required
                />
                <textarea
                  name="features"
                  value={newPumpData.features}
                  onChange={handleInputChange}
                  placeholder="Features (comma-separated)"
                  required
                />
                <div className="file-input-container">
                  <label>Main Image:</label>
                  <input 
                    type="file" 
                    onChange={handleMultipleImageUpload} 
                    required 
                  />
                </div>
                <div className="file-input-container">
                  <label>Right Image:</label>
                  <input 
                    type="file" 
                    onChange={handleRightImageUpload} 
                    required 
                  />
                </div>

                <div className="form-buttons">
                  <button type="submit" className="submit-btn">
                    Add Pump
                  </button>
                  <button 
                    type="button" 
                    className="cancel-btn" 
                    onClick={() => setShowAddForm(false)}
                  >
                    Cancel
                  </button>
                </div>
              </form>
            </div>
          )}

          <div className="pump-list">
            {(currentView === 'residential' ? residentialPumps : agriculturalPumps).map((pump) => (
              <div key={pump.id} className="pump-card">
                <img src={pump.image} alt={pump.name} />
                <h3>{pump.name}</h3>
                <p>{pump.price}</p>
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeletePump(pump.id, currentView)}
                >
                  Delete
                </button>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default AdminPortal;
