import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css'; 

const PumpDetail = () => {
    const { pumpId } = useParams(); // Extract pumpId from URL
    const navigate = useNavigate();

    const [pump, setPump] = useState(null); // State to hold pump data
    const [loading, setLoading] = useState(true); // Loading state
    const [error, setError] = useState(null); // Error state
    const [agriculturalPumps, setAgriculturalPumps] = useState([]); // State for agricultural pumps
    const [showAgriculturalDetails, setShowAgriculturalDetails] = useState(false); // Toggle for Read More button

    // Fetch pump details based on the pumpId
    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                // Call the API to fetch pump data based on the ID from the URL
                const response = await fetch(`http://localhost:5001/api/pumps/${pumpId}`);

                if (!response.ok) {
                    throw new Error('Pump not found');
                }

                const data = await response.json();
                setPump(data.pump); // Set the pump data from the API (data.pump)
                setLoading(false); // Set loading to false once data is fetched
            } catch (err) {
                setError(err.message); // Handle error if data fetch fails
                setLoading(false);
            }
        };

        fetchPumpDetails(); // Call the function to fetch pump details
    }, [pumpId]); // Re-run the effect when pumpId changes

    // Fetch agricultural pumps when the "Read More" button is clicked
    const fetchAgriculturalPumps = async () => {
        try {
            const response = await fetch('http://localhost:5001/api/pumps/agricultural');

            if (!response.ok) {
                throw new Error('Failed to fetch agricultural pumps');
            }

            const data = await response.json();
            setAgriculturalPumps(data.pumps); // Set the agricultural pumps data
            setShowAgriculturalDetails(true); // Show the details when the data is loaded
        } catch (err) {
            setError(err.message); // Handle error if data fetch fails
        }
    };

    if (loading) {
        return <div>Loading...</div>; // Show loading message while fetching data
    }

    if (error) {
        return <div>{error}. Please go back to the <a href="/products">products page</a>.</div>; // Show error message if pump not found
    }

    if (!pump) {
        return <div>Pump not found. Please go back to the <a href="/products">products page</a>.</div>; // Fallback if no pump data
    }

    // Create an array of pump IDs in the order they appear
    const pumpIds = ['v4', 'v5', 'v6']; // You can dynamically fetch this list based on available pumps in your database

    // Find the index of the current pump
    const currentIndex = pumpIds.indexOf(pumpId);

    // Determine the previous and next pump IDs (circular navigation)
    const prevPump = pumpIds[(currentIndex - 1 + pumpIds.length) % pumpIds.length];
    const nextPump = pumpIds[(currentIndex + 1) % pumpIds.length];

    // Function to navigate to a specific pump detail
    const handleNavigation = (id) => {
        navigate(`/pumpDetail/${id}`);
    };

    return (
        <div className="body">
            {/* Product Description */}
            <div className="product-description">
                {/* Ensure image URL is correct */}
                <img 
                    src={`http://localhost:5001/uploads/${pump.image}`} 
                    className="product-image" 
                    alt={pump.name} 
                />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                pump.features.map((feature, index) => (
                                    <li key={index}>{feature}</li>
                                ))
                            ) : (
                                <li>No features available.</li> // Fallback message if features are empty or undefined
                            )}
                        </ul>
                    </div>

                    <a 
                        href="https://lallytradingcompany.onrender.com" 
                        className="buy-button"
                    >
                        Send Enquiry
                    </a>
                </div>
            </div>

            {/* Image for right view of the pump */}
            <div className="chart">
                <img 
                    src={`http://localhost:5001/uploads/${pump.rightImage}`} 
                    alt="Right view of pump" 
                />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => handleNavigation(prevPump)}
                >
                    &#8592; {/* Left Arrow (back) */}
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump)}
                >
                    &#8594; {/* Right Arrow (forward) */}
                </button>
            </div>

            {/* Read More Button */}
            <div className="read-more-section">
                <button 
                    onClick={fetchAgriculturalPumps} 
                    className="read-more-btn"
                >
                    Read More (Agricultural Pumps)
                </button>

                {/* Display Agricultural Pumps when the button is clicked */}
                {showAgriculturalDetails && (
                    <div className="agriculture-pumps">
                        <h2>Agricultural Pumps</h2>
                        {agriculturalPumps.length > 0 ? (
                            agriculturalPumps.map((pump) => (
                                <div key={pump._id} className="agricultural-pump">
                                    <h3>{pump.name}</h3>
                                    <p>{pump.price}</p>
                                    <img 
                                        src={`http://localhost:5001/uploads/${pump.image}`} 
                                        alt={pump.name} 
                                        style={{ width: '200px', height: 'auto' }} 
                                    />
                                </div>
                            ))
                        ) : (
                            <p>No agricultural pumps found.</p>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default PumpDetail;
