import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPortal = () => {
  const [pumps, setPumps] = useState([]); // State to hold pumps data
  const [newPump, setNewPump] = useState({ name: '', price: '', link: '' }); // State for new pump form
  const [image, setImage] = useState(null); // State for uploaded image
  const [error, setError] = useState(''); // Error message state

  // Fetch pumps from the backend
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        setPumps(response.data.pumps); // Set pumps from response
      } catch (err) {
        console.error('Error fetching pumps:', err);
        setError('Failed to fetch pumps.');
      }
    };

    fetchPumps();
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      const formData = new FormData();
      formData.append('image', file);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.filename); // Store uploaded image filename
        setError('');
      } catch (err) {
        console.error('Error uploading image:', err);
        setError('Error uploading image.');
      }
    }
  };

  // Handle adding a new pump
  const handleAddPump = async () => {
    if (!newPump.name || !newPump.price || !image) {
      setError('Please fill all fields and upload an image.');
      return;
    }

    try {
      const pump = { ...newPump, image };
      const response = await axios.post('http://localhost:5001/api/pumps', pump);
      setPumps([...pumps, response.data.pump]); // Add the new pump to state
      setNewPump({ name: '', price: '', link: '' }); // Reset the form
      setImage(null); // Clear the image
      setError('');
    } catch (err) {
      console.error('Error adding pump:', err);
      setError('Error adding pump.');
    }
  };

  // Handle removing a pump
  const handleRemovePump = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      setPumps(pumps.filter((pump) => pump._id !== id)); // Remove the pump from state
    } catch (err) {
      console.error('Error removing pump:', err);
      setError('Error removing pump.');
    }
  };

  return (
    <div>
      <h1>Admin Portal</h1>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      {/* Add Pump Form */}
      <h2>Add New Pump</h2>
      <input
        type="text"
        placeholder="Name"
        value={newPump.name}
        onChange={(e) => setNewPump({ ...newPump, name: e.target.value })}
      />
      <input
        type="text"
        placeholder="Price"
        value={newPump.price}
        onChange={(e) => setNewPump({ ...newPump, price: e.target.value })}
      />
      <input
        type="text"
        placeholder="Link (optional)"
        value={newPump.link}
        onChange={(e) => setNewPump({ ...newPump, link: e.target.value })}
      />
      <input type="file" onChange={handleImageUpload} />
      <button onClick={handleAddPump}>Add Pump</button>

      {/* Display Existing Pumps */}
      <h2>Existing Pumps</h2>
      {pumps.length === 0 ? (
        <p>No pumps available.</p>
      ) : (
        pumps.map((pump) => (
          <div key={pump._id} style={{ border: '1px solid #ccc', margin: '10px', padding: '10px' }}>
            <img
              src={`http://localhost:5001/uploads/${pump.image}`}
              alt={pump.name}
              style={{ width: '100px', height: '100px' }}
            />
            <div>
              <p><strong>Name:</strong> {pump.name}</p>
              <p><strong>Price:</strong> {pump.price}</p>
              {pump.link && <p><strong>Link:</strong> <a href={pump.link}>{pump.link}</a></p>}
              <button onClick={() => handleRemovePump(pump._id)}>Remove</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminPortal;
