import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../css/Admin.css';

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [error, setError] = useState(null);
  const [currentView, setCurrentView] = useState('residential');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newPumpData, setNewPumpData] = useState({
    id: '',
    name: '',
    price: '',
    features: '',
  });
  const [selectedFiles, setSelectedFiles] = useState({
    image: null,
    rightImage: null
  });

  // Fetch residential pumps data
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/pumps')
      .then((response) => setResidentialPumps(response.data))
      .catch((error) => console.error('Error fetching residential pumps:', error));
  }, []);

  // Fetch agricultural pumps data
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/agpumps')
      .then((response) => setAgriculturalPumps(response.data))
      .catch((error) => console.error('Error fetching agricultural pumps:', error));
  }, []);

  // Handle file selection for both image types
  const handleFileSelect = (e, fileType) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFiles(prev => ({
        ...prev,
        [fileType]: file
      }));
    }
  };

  // Handle new pump form input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPumpData((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  // Handle adding new pump to the database
  const handleAddPump = async (e) => {
    e.preventDefault();
    setError(null);

    // Validate required fields
    if (!newPumpData.id || !newPumpData.name || !newPumpData.price || !newPumpData.features) {
      setError('Please fill in all required fields');
      return;
    }

    // Create FormData object
    const formData = new FormData();

    // Append text data
    formData.append('id', newPumpData.id);
    formData.append('name', newPumpData.name);
    formData.append('price', newPumpData.price);

    // Convert features string to array and stringify
    const featuresArray = newPumpData.features.split(',').map(feature => feature.trim());
    formData.append('features', JSON.stringify(featuresArray));

    // Append files
    if (selectedFiles.image) {
      formData.append('image', selectedFiles.image);
    }
    if (selectedFiles.rightImage) {
      formData.append('rightImage', selectedFiles.rightImage);
    }

    try {
      // Determine which endpoint to use based on current view
      const endpoint = currentView === 'residential' 
        ? 'http://localhost:5001/api/pumps'
        : 'http://localhost:5001/api/agpumps';

      const response = await axios.post(endpoint, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        }
      });

      // Refresh the pump list
      if (currentView === 'residential') {
        const updatedPumps = await axios.get('http://localhost:5001/api/pumps');
        setResidentialPumps(updatedPumps.data);
      } else {
        const updatedPumps = await axios.get('http://localhost:5001/api/agpumps');
        setAgriculturalPumps(updatedPumps.data);
      }

      // Reset form
      setNewPumpData({
        id: '',
        name: '',
        price: '',
        features: '',
      });
      setSelectedFiles({
        image: null,
        rightImage: null
      });
      setShowAddForm(false);
      setError(null);

    } catch (error) {
      console.error('Error adding new pump:', error);
      setError(error.response?.data?.error || 'Error adding new pump. Please try again.');
    }
  };

  // Handle delete pump functionality
  const handleDeletePump = async (id) => {
    if (!window.confirm('Are you sure you want to delete this pump?')) {
      return;
    }

    try {
      if (currentView === 'residential') {
        await axios.delete(`http://localhost:5001/api/pumps/${id}`);
        const updatedPumps = residentialPumps.filter(pump => pump.id !== id);
        setResidentialPumps(updatedPumps);
      } else {
        await axios.delete(`http://localhost:5001/api/agpumps/${id}`);
        const updatedPumps = agriculturalPumps.filter(pump => pump.id !== id);
        setAgriculturalPumps(updatedPumps);
      }
    } catch (error) {
      console.error('Error deleting pump:', error);
      setError('Error deleting pump.');
    }
  };

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>

      {/* Sidebar for toggling views */}
      <div className="sidebar">
        <button
          className={`sidebar-btn ${currentView === 'residential' ? 'active' : ''}`}
          onClick={() => setCurrentView('residential')}
        >
          Residential Pumps
        </button>
        <button
          className={`sidebar-btn ${currentView === 'agricultural' ? 'active' : ''}`}
          onClick={() => setCurrentView('agricultural')}
        >
          Agricultural Pumps
        </button>
      </div>

      {/* Add New Pump Catalog */}
      <div className="gallery-container">
        <div className="add-new-pump" onClick={() => setShowAddForm(true)}>
          <span className="plus-sign">+</span>
          <p className="add-text">Add New Pump</p>
        </div>
      </div>

      {/* Add Pump Form */}
      {showAddForm && (
        <div className="add-pump-form">
          <h2>Add New {currentView === 'residential' ? 'Residential' : 'Agricultural'} Pump</h2>
          {error && <p className="error">{error}</p>}
          <form onSubmit={handleAddPump}>
            <input
              type="text"
              name="id"
              value={newPumpData.id}
              onChange={handleInputChange}
              placeholder="Pump ID"
              required
            />
            <input
              type="text"
              name="name"
              value={newPumpData.name}
              onChange={handleInputChange}
              placeholder="Pump Name"
              required
            />
            <input
              type="text"
              name="price"
              value={newPumpData.price}
              onChange={handleInputChange}
              placeholder="Pump Price"
              required
            />
            <textarea
              name="features"
              value={newPumpData.features}
              onChange={handleInputChange}
              placeholder="Features (comma-separated)"
              required
            />
            <div className="file-input-container">
              <label>Main Image:</label>
              <input 
                type="file" 
                onChange={(e) => handleFileSelect(e, 'image')} 
                accept="image/*"
                required 
              />
            </div>
            <div className="file-input-container">
              <label>Right Image:</label>
              <input 
                type="file" 
                onChange={(e) => handleFileSelect(e, 'rightImage')} 
                accept="image/*"
                required 
              />
            </div>
            <div className="form-buttons">
              <button type="submit">Add Pump</button>
              <button type="button" onClick={() => setShowAddForm(false)}>Cancel</button>
            </div>
          </form>
        </div>
      )}

      {/* Pump Gallery */}
      <div className="gallery-container">
        <div className="pump-gallery">
          {currentView === 'residential' && (
            <>
              <h2>Residential Pumps</h2>
              {residentialPumps.length > 0 ? (
                residentialPumps.map((pump) => (
                  <div key={pump.id} className="pump-item animate-on-scroll">
                    <img 
                      src={`http://localhost:5001/uploads/${pump.image}`} 
                      alt={pump.name}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.jpg';
                      }}
                    />
                    <div className="pump-info">
                      <p className="pump-name">{pump.name}</p>
                      <p className="pump-price">{pump.price}</p>
                      <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">
                        Read more
                      </Link>
                      <button 
                        className="delete-btn" 
                        onClick={() => handleDeletePump(pump.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <p>Loading Residential Pumps...</p>
              )}
            </>
          )}

          {currentView === 'agricultural' && (
            <>
              <h2>Agricultural Pumps</h2>
              {agriculturalPumps.length > 0 ? (
                agriculturalPumps.map((pump) => (
                  <div key={pump.id} className="pump-item animate-on-scroll">
                    <img 
                      src={`http://localhost:5001/uploads/${pump.image}`} 
                      alt={pump.name}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = '/placeholder-image.jpg';
                      }}
                    />
                    <div className="pump-info">
                      <p className="pump-name">{pump.name}</p>
                      <p className="pump-price">{pump.price}</p>
                      <Link to={`/pumpDetails/${pump.id}`} className="read-more-btn">
                        Read more
                      </Link>
                      <button 
                        className="delete-btn" 
                        onClick={() => handleDeletePump(pump.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))
              ) : (
                <p>Loading Agricultural Pumps...</p>
              )}
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminPortal;
