import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css'; 
import { pumps } from '../data/pumps';

const PumpDetail = () => {
    const { pumpId } = useParams();
    const navigate = useNavigate();

    // Ensure pumpId is valid
    const pump = pumps.find(p => p.id === pumpId);

    if (!pump) {
        // If pumpId is invalid, show a 404 or fallback message
        return <div>Pump not found. Please go back to products page.</div>;
    }

    // Create an array of pump IDs (ordered)
    const pumpIds = pumps.map(p => p.id);

    // Find the current pump's index
    const currentIndex = pumpIds.indexOf(pumpId);

    // Get the next and previous pump in the array (circular navigation)
    const prevPump = pumpIds[(currentIndex - 1 + pumpIds.length) % pumpIds.length]; // Wrap around if at the start
    const nextPump = pumpIds[(currentIndex + 1) % pumpIds.length]; // Wrap around if at the end

    // Function to navigate to a different pump
    const handleNavigation = (id) => {
        navigate(`/pumpDetail/${id}`);
    };

    return (
        <div className="body">

            <div className="product-description">
                <img src={pump.image} className="product-image" alt={pump.name} />
                <div>
                    <h1>{pump.name}</h1>
                    <p><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>

                    <div className="features">
                        <h2>Features</h2>
                        <ul>
                            {pump.features.map((feature, index) => (
                                <li key={index}>{feature}</li>
                            ))}
                        </ul>
                    </div>

                    <a href="https://lallytradingcompany.onrender.com" className="buy-button">Send Enquiry</a>
                </div>
            </div>

            <div className="chart">
                <img src={pump.rightImage} alt="Right view of pump" />
            </div>

            {/* Navigation Buttons */}
            <div className="navigation-buttons">
                <button 
                    className="nav-btn left-btn"
                    onClick={() => handleNavigation(prevPump)}
                >
                    &#8592; {/* Left Arrow (back) */}
                </button>
                <button 
                    className="nav-btn right-btn"
                    onClick={() => handleNavigation(nextPump)}
                >
                    &#8594; {/* Right Arrow (forward) */}
                </button>
            </div>
        </div>
    );
};

export default PumpDetail;
