import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../css/Admin.css'; 

const AdminPortal = () => {
  const [residentialPumps, setResidentialPumps] = useState([]);
  const [agriculturalPumps, setAgriculturalPumps] = useState([]);
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);
  const [pumpData, setPumpData] = useState({
    name: '',
    price: '',
    images: []
  });
  const [showAddForm, setShowAddForm] = useState(false);

  // Fetch residential and agricultural pumps data from backend
  useEffect(() => {
    axios
      .get('http://localhost:5001/api/pumps')
      .then((response) => setResidentialPumps(response.data))
      .catch((error) => console.error('Error fetching residential pumps:', error));
  }, []);

  useEffect(() => {
    axios
      .get('http://localhost:5001/api/agpumps')
      .then((response) => setAgriculturalPumps(response.data))
      .catch((error) => console.error('Error fetching agricultural pumps:', error));
  }, []);

  // Handle image upload
  const handleImageUpload = async (e) => {
    const files = e.target.files;
    const formData = new FormData();

    if (files.length > 0) {
      formData.append('images', files[0]);
      formData.append('images', files[1]);

      try {
        const response = await axios.post('http://localhost:5001/api/upload', formData);
        setImage(response.data.filePaths[0]);
        setPumpData({ ...pumpData, images: response.data.filePaths });
      } catch (err) {
        console.error('Error uploading images:', err);
        setError('Error uploading images.');
      }
    }
  };

  // Handle adding new pump
  const handleAddPump = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', pumpData.name);
    formData.append('price', pumpData.price);

    pumpData.images.forEach((image) => {
      formData.append('images', image);
    });

    try {
      const response = await axios.post('http://localhost:5001/api/pumps/add', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      console.log('Pump added successfully:', response.data);
      setShowAddForm(false);  // Hide the form after submission
      // Optionally, reload the pumps list to include the new pump
    } catch (err) {
      console.error('Error adding new pump:', err);
    }
  };

  // Handle deleting a pump
  const handleDeletePump = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:5001/api/pumps/${id}`);
      console.log('Pump deleted:', response.data);
      setResidentialPumps(residentialPumps.filter(pump => pump._id !== id));  // Update local state
    } catch (err) {
      console.error('Error deleting pump:', err);
    }
  };

  return (
    <div className="admin-portal">
      <h1>Admin Portal</h1>

      {/* Button to toggle Add New Pump form */}
      <button onClick={() => setShowAddForm(!showAddForm)} className="add-pump-btn">
        + Add New Pump
      </button>

      {/* Show Add Pump Form if showAddForm is true */}
      {showAddForm && (
        <div className="add-pump-form">
          <form onSubmit={handleAddPump}>
            <input 
              type="text" 
              placeholder="Pump Name" 
              value={pumpData.name} 
              onChange={(e) => setPumpData({ ...pumpData, name: e.target.value })} 
              required 
            />
            <input 
              type="number" 
              placeholder="Pump Price" 
              value={pumpData.price} 
              onChange={(e) => setPumpData({ ...pumpData, price: e.target.value })} 
              required 
            />
            <input type="file" onChange={handleImageUpload} multiple />
            {error && <p className="error">{error}</p>}
            <button type="submit">Add Pump</button>
          </form>
        </div>
      )}

      {/* Pump Gallery Container */}
      <div className="pump-gallery">
        {residentialPumps.length > 0 ? (
          residentialPumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <img src={`http://localhost:5001/uploads/${pump.images[0]}`} alt={pump.name} />
              <div className="pump-info">
                <p className="pump-name">{pump.name}</p>
                <p className="pump-price">{pump.price}</p>
                <Link to={`/pumpDetail/${pump._id}`} className="read-more-btn">Read more</Link>
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeletePump(pump._id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>No Residential Pumps available.</p>
        )}
      </div>
    </div>
  );
};

export default AdminPortal;
