import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Card } from "@/components/ui/card";

const AgPumpDetail = () => {
    const { pumpId } = useParams();
    const navigate = useNavigate();
    const [pump, setPump] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [pumps, setPumps] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:5001/api/agpumps')
            .then(response => setPumps(response.data))
            .catch(error => console.error('Error fetching agricultural pumps:', error));
    }, []);

    useEffect(() => {
        const fetchPumpDetails = async () => {
            try {
                const response = await fetch(`http://localhost:5001/api/agpumps/${pumpId}`);
                if (!response.ok) {
                    throw new Error('Agricultural pump not found');
                }
                const data = await response.json();
                setPump(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };
        fetchPumpDetails();
    }, [pumpId]);

    if (loading) return <div className="flex justify-center items-center min-h-screen">Loading...</div>;
    if (error) return <div className="flex justify-center items-center min-h-screen">{error}. Please go back to the <a href="/home" className="text-blue-500 hover:underline ml-1">Home page</a>.</div>;
    if (!pump) return <div className="flex justify-center items-center min-h-screen">Agricultural pump not found. Please go back to the <a href="/home" className="text-blue-500 hover:underline ml-1">Home page</a>.</div>;

    const pumpIds = pumps.map(pump => pump.id);
    const currentIndex = pumpIds.indexOf(pumpId);
    const prevPump = pumpIds[currentIndex - 1] || pumpIds[pumpIds.length - 1];
    const nextPump = pumpIds[currentIndex + 1] || pumpIds[0];

    const handleNavigation = (id) => {
        navigate(`/agPumpDetail/${id}`);
    };

    return (
        <div className="max-w-6xl mx-auto px-4 py-8">
            <Card className="overflow-hidden">
                <div className="grid md:grid-cols-2 gap-8 p-6">
                    {/* Left side - Product Image and Details */}
                    <div className="space-y-6">
                        <div className="relative aspect-square overflow-hidden rounded-lg bg-gray-100">
                            <img 
                                src={`http://localhost:5001${pump.image}`}
                                className="object-contain w-full h-full"
                                alt={pump.name}
                                onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = '/placeholder-image.png';
                                }}
                            />
                        </div>
                        <div className="space-y-4">
                            <h1 className="text-2xl font-bold">{pump.name}</h1>
                            <p className="text-lg"><strong>MRP:</strong> {pump.price} (Inclusive of all taxes)</p>
                            <a 
                                href="https://lallytradingcompany.onrender.com"
                                className="inline-block px-6 py-3 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors"
                            >
                                Send Enquiry
                            </a>
                        </div>
                    </div>

                    {/* Right side - Features and Right View Image */}
                    <div className="space-y-6">
                        <div className="bg-gray-50 rounded-lg p-4">
                            <h2 className="text-xl font-semibold mb-4">Features</h2>
                            <ul className="space-y-2">
                                {Array.isArray(pump.features) && pump.features.length > 0 ? (
                                    pump.features.map((feature, index) => (
                                        <li key={index} className="flex items-start">
                                            <span className="mr-2">•</span>
                                            {feature}
                                        </li>
                                    ))
                                ) : (
                                    <li>No features available.</li>
                                )}
                            </ul>
                        </div>
                        <div className="relative aspect-square overflow-hidden rounded-lg bg-gray-100">
                            <img 
                                src={`http://localhost:5001${pump.rightImage}`}
                                className="object-contain w-full h-full"
                                alt="Right view of agricultural pump"
                                onError={(e) => {
                                    e.target.onerror = null;
                                    e.target.src = '/placeholder-image.png';
                                }}
                            />
                        </div>
                    </div>
                </div>
            </Card>

            {/* Navigation Buttons */}
            <div className="flex justify-between mt-6">
                <button 
                    onClick={() => handleNavigation(prevPump)}
                    className="p-2 rounded-full bg-gray-100 hover:bg-gray-200 transition-colors"
                >
                    ←
                </button>
                <button 
                    onClick={() => handleNavigation(nextPump)}
                    className="p-2 rounded-full bg-gray-100 hover:bg-gray-200 transition-colors"
                >
                    →
                </button>
            </div>
        </div>
    );
};

export default pumpDetail;
