import { useParams } from 'react-router-dom';

const PumpDetail = () => {
  const { pumpId } = useParams();  // Extract pumpId from URL
  console.log('Pump ID:', pumpId); // Check if pumpId is correct

  const [pump, setPump] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchPumpDetails = async () => {
      if (!pumpId) {
        setError('Pump ID is missing.');
        setLoading(false);
        return;
      }

      try {
        const response = await fetch(`http://localhost:5001/api/pumps/${pumpId}`);
        const data = await response.json();

        if (response.ok && data) {
          setPump(data);
        } else {
          setError('Pump not found.');
        }
      } catch (err) {
        setError('Error fetching pump details.');
      } finally {
        setLoading(false);
      }
    };

    fetchPumpDetails();
  }, [pumpId]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  if (!pump) return <p>No pump details available.</p>;

  return (
    <div>
      <h1>{pump.name}</h1>
      <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
      <p>Price: {pump.price}</p>
      <ul>
        {pump.features.map((feature, idx) => (
          <li key={idx}>{feature}</li>
        ))}
      </ul>
      <button onClick={() => window.history.back()}>Back</button>
    </div>
  );
};

export default PumpDetail;
