import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../css/styles.css';

function Home() {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch pumps from the database when the component mounts
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/pumps');
        console.log('Fetched Pumps:', response.data);  // Log response
        setPumps(response.data);
      } catch (err) {
        console.error('Error fetching pumps:', err);
        setError('Failed to fetch pumps.');
      } finally {
        setLoading(false);
      }
    };

    fetchPumps();
  }, []);

  return (
    <div className="main-content">
      <h2>Our Pumps</h2>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}

      <div className="pump-gallery">
        {pumps.length > 0 ? (
          pumps.map((pump) => (
            <div key={pump._id} className="pump-item">
              <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
              <div className="pump-info">
                <p>{pump.name}</p>
                <p>{pump.price}</p>
              </div>
            </div>
          ))
        ) : (
          <p>No pumps available.</p>
        )}
      </div>
    </div>
  );
}

export default Home;
