import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import v4Image from '../images/v4.png';
import v42Image from '../images/v42.png';
import v3Image from '../images/v3.png';
import v32Image from '../images/v32.png';
import v40Image from '../images/v40.png';
import owImage from '../images/ow.png';
import ow1Image from '../images/OW1.png';
import prod from '../images/prod.png';
import pump2 from '../images/pump2.png';
import bg from '../images/bg2.jpg';
import bg1 from '../images/PUMP.jpg';
import '../css/styles.css';

function Home() {
  const [pumps, setPumps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Set up the image slider
  const images = [bg1];
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval); // Clear the interval on component unmount
  }, [images.length]);

  // Fetch pump data from the backend API
  useEffect(() => {
    const fetchPumps = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/pumps'); // Adjust your API endpoint here
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPumps(data); // Set the pumps data to state
        setLoading(false);
      } catch (error) {
        console.error('Error fetching pumps:', error);
        setError(`Failed to load pumps data: ${error.message}`);
        setLoading(false);
      }
    };
    fetchPumps();
  }, []); // Empty dependency array ensures this runs once on component mount

  // Intersection observer for scroll animations
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('slide-in');
          }
        });
      },
      { threshold: 0.1 }
    );

    const elements = document.querySelectorAll('.animate-on-scroll');
    elements.forEach((el) => observer.observe(el));

    return () => observer.disconnect();
  }, []);

  return (
    <div className="main-content">
      {/* Main Image Slider */}
      <div className="image-section1 animate-on-scroll">
        <div className="slider">
          <img src={images[currentIndex]} alt="Main Display" className="slider-image" />
        </div>
      </div>

      {/* Description Section */}
      <div className="b animate-on-scroll">
        <center>
          <p className="b2">
            Explore our range of Agricultural submersible pumps <br />
            The product range covers a wide application such as agriculture,
            water supply to townships, Industries, sewage and drainage.
          </p>
          <Link to="/products" className="btn">View Products</Link>
        </center>
      </div>

      {/* Pumps Section */}
      <h2 className="section-title animate-on-scroll">Residential Submersible Pumps</h2>
      <div className="pump-gallery" id="pumpGallery">
        {loading ? (
          <div>Loading pumps...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          pumps.length > 0 ? (
            pumps.map((pump) => (
              <div key={pump.id} className="pump-item animate-on-scroll">
                <img src={`http://localhost:5001/uploads/${pump.image}`} alt={pump.name} />
                <div className="pump-info">
                  <p className="pump-name">{pump.name}</p>
                  <p className="pump-price">{pump.price}</p>
                  <Link to={`/pumpDetail/${pump.id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))
          ) : (
            <div>No pumps available.</div>
          )
        )}
      </div>

      {/* About Section */}
      <div className="container2 animate-on-scroll">
        <div className="content">
          <center>
            <h3 style={{ marginRight: '70px', fontSize: '20px' }}>About our company</h3>
            <h2 style={{ textAlign: 'center' }}>We provide professional solutions to choose a suitable pump</h2>
            <p style={{ marginRight: '55px' }}>
              The product range covers a wide application<br />
              such as domestic, agriculture, water supply to <br />
              townships, Industries, sewage and drainage.
            </p>
          </center>
          <div className="side-sections">
            <div className="side-section">
              <h1>12</h1>
              <p>In-Progress Sites.</p>
            </div>
            <div className="side-section">
              <h1>12k+</h1>
              <p>pumps sold.</p>
            </div>
          </div>
        </div>
        <div className="image-container">
          <img src={pump2} alt="Pump Showcase" />
        </div>
      </div>

      {/* Footer or Additional Section */}
      <div className="new animate-on-scroll">
        <h5>We Believe Every Client Is a<br />Valuable Long-Term Partner</h5>
      </div>
      <div className="prod animate-on-scroll">
        <img src={prod} alt="Product Display" />
      </div>
    </div>
  );
}

export default Home;
