import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../css/read.css';

const PumpDetail = () => {
  const { pumpId } = useParams();
  const navigate = useNavigate();
  const [pump, setPump] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch pump details by ID
  useEffect(() => {
    console.log('Fetching pump details for ID:', pumpId);
    fetch(` http://localhost:5001/api/pump/<pumpId>`)
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched pump details:', data);
        if (data.success) {
          setPump(data.pump);
        } else {
          setError('Pump not found');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching pump details:', err);
        setError('Failed to load pump details');
        setLoading(false);
      });
  }, [pumpId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="body">
      <div className="product-description">
        <img
          src={`http://localhost:5001/uploads/${pump.image}`}
          className="product-image"
          alt={pump.name}
        />
        <div>
          <h1>{pump.name}</h1>
          <p>
            <strong>MRP:</strong> {pump.price} (Inclusive of all taxes)
          </p>

          <div className="features">
            <h2>Features</h2>
            <ul>
              {pump.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>

          <a href="https://lallytradingcompany.onrender.com" className="buy-button">
            Send Enquiry
          </a>
        </div>
      </div>

      <div className="chart">
        <img
          src={`http://localhost:5001/uploads/${pump.rightImage}`}
          alt="Right view of pump"
        />
      </div>

      <div className="navigation-buttons">
        <button
          className="nav-btn left-btn"
          onClick={() => navigate(`/pumpDetail/${pump.previousId}`)}
        >
          &#8592;
        </button>
        <button
          className="nav-btn right-btn"
          onClick={() => navigate(`/pumpDetail/${pump.nextId}`)}
        >
          &#8594;
        </button>
      </div>
    </div>
  );
};

export default PumpDetail;
