import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../css/read.css';

const PumpDetail = () => {
  const { pumpId } = useParams();
  const [pump, setPump] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch pump details by ID
  useEffect(() => {
    fetch(`http://localhost:5001/api/pumps/${pumpId}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setPump(data.pump);
        } else {
          setError('Pump not found');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching pump details:', err);
        setError('Failed to load pump details');
        setLoading(false);
      });
  }, [pumpId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }
  useEffect(() => {
    console.log(`Fetching pump details for ID: ${pumpId}`);
    fetch(`http://localhost:5001/api/pumps/${pumpId}`)
      .then((response) => response.json())
      .then((data) => {
        console.log('Pump detail response:', data);
        if (data.success) {
          setPump(data.pump);
        } else {
          setError('Pump not found');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching pump details:', err);
        setError('Failed to load pump details');
        setLoading(false);
      });
  }, [pumpId]);

  return (
    <div className="body">
      <div className="product-description">
        <img
          src={`http://localhost:5001/uploads/${pump.image}`}
          className="product-image"
          alt={pump.name}
        />
        <div>
          <h1>{pump.name}</h1>
          <p>
            <strong>Price:</strong> {pump.price}
          </p>
          <div className="features">
            <h2>Features</h2>
            <ul>
              {pump.features.map((feature, index) => (
                <li key={index}>{feature}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div className="chart">
        <img
          src={`http://localhost:5001/uploads/${pump.rightImage}`}
          alt="Right view of pump"
        />
      </div>
    </div>
  );
};

export default PumpDetail;
