import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import bg1 from '../images/PUMP.jpg';
import prod from '../images/prod.png';
import pump2 from '../images/pump2.png';
import '../css/styles.css';

function Home() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [products, setProducts] = useState([]); // State to hold products

  // Set up the image slider
  const images = [bg1];
  const [currentIndex, setCurrentIndex] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval); // Clear the interval on component unmount
  }, [images.length]);

  // Fetch products from the backend API
  useEffect(() => {
    fetch('http://localhost:5001/api/products')
      .then((response) => response.json())
      .then((data) => {
        if (data.success) {
          setProducts(data.products); // Set products data to state
        } else {
          setError(data.message); // Set error message
        }
        setLoading(false); // Set loading to false once data is fetched
      })
      .catch((error) => {
        console.error('Error fetching products:', error);
        setError('Failed to load products.'); // Set error message in case of failure
        setLoading(false); // Stop loading in case of error
      });
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div className="main-content">
      {/* Main Image Slider */}
      <div className="image-section1 animate-on-scroll">
        <div className="slider">
          <img src={images[currentIndex]} alt="Main Display" className="slider-image" />
        </div>
      </div>

      {/* Description Section */}
      <div className="b animate-on-scroll">
        <center>
          <p className="b2">
            Explore our range of Agricultural submersible pumps <br />
            The product range covers a wide application such as agriculture,
            water supply to townships, Industries, sewage and drainage.
          </p>
          <Link to="/products" className="btn">View Products</Link>
        </center>
      </div>

      {/* Products Section */}
      <h2 className="section-title animate-on-scroll">Our Products</h2>
      <div className="product-gallery" id="productGallery">
        {loading ? (
          <div>Loading products...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          products.length > 0 ? (
            products.map((product) => (
              <div key={product._id} className="product-item animate-on-scroll">
                <div className="product-images">
                  {product.image && product.image.map((imgUrl, index) => (
                    <img key={index} src={imgUrl} alt={`${product.name} - ${index}`} />
                  ))}
                </div>
                <div className="product-info">
                  <p className="product-name">{product.name}</p>
                  <p className="product-price">{`₹${product.price}`}</p>
                  <Link to={`/productDetail/${product._id}`} className="read-more-btn">Read more</Link>
                </div>
              </div>
            ))
          ) : (
            <div>No products available.</div>
          )
        )}
      </div>

      {/* Footer or Additional Section */}
      <div className="new animate-on-scroll">
        <h5>We Believe Every Client Is a<br />Valuable Long-Term Partner</h5>
      </div>
      <div className="prod animate-on-scroll">
        <img src={prod} alt="Product Display" />
      </div>
    </div>
  );
}

export default Home;
