import React from 'react';
import { useParams } from 'react-router-dom';

const PumpDetail = () => {
    const { pumpId } = useParams(); // Get pumpId from URL parameters
    const pumps = [
        { id: 1, name: 'Pump A', features: ['Feature 1', 'Feature 2', 'Feature 3'], image: '../images/v4.png', rightImage: '../images/v4.png' },
        { id: 2, name: 'Pump B', features: ['Feature 1', 'Feature 2', 'Feature 3'], image: 'path/to/pumpB.jpg', rightImage: 'path/to/rightImageB.jpg' }
    ];

    const pump = pumps.find(p => p.id === parseInt(pumpId)); // Convert pumpId to an integer
    if (!pump) {
        return <div>Pump not found</div>;
    }

    return (
        <div>
            <h1>{pump.name}</h1>
            <img src={pump.image} alt={pump.name} />
            <ul>
                {pump.features.map((feature, index) => (
                    <li key={index}>{feature}</li>
                ))}
            </ul>
        </div>
    );
};

export default PumpDetail;
